import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProductPage = () => {
    const [products, setProducts] = useState([]);
    const [newProduct, setNewProduct] = useState({});

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async ()  => {
        try {
            const response = await axios.get('/api/products');
            setProducts(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleInputChange = (e) => {
        setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
    };

    const handleAddProduct = async () => {
        try {
            await axios.post('/api/products', newProduct);
            fetchProducts(); // Refresh product list after adding
            setNewProduct({}); // Clear input fields
        } catch (error) {
            console.error('Error adding product:', error);
        }
    };

    const handleDeleteProduct = async (productId) => {
        try {
            await axios.delete(`/api/products/${productId}`);
            fetchProducts(); // Refresh product list after deletion
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    return (
        <div>
            <h1>Products</h1>

            {/* Display Products */}
            <ul>
                {products.map((product) => (
                    <li key={product.productId}>
                        {product.name} - {product.price}
                        <button onClick={() => handleDeleteProduct(product.productId)}>Delete</button>
                    </li>
                ))}
            </ul>

            {/* Create Product Form */}
            <h2>Add New Product</h2>
            <input type="text" name="name" placeholder="Name" value={newProduct.name || ''} onChange={handleInputChange} />
            <input type="text" name="price" placeholder="Price" value={newProduct.price || ''} onChange={handleInputChange} />
            <button onClick={handleAddProduct}>Add Product</button>
        </div>
    );
};

export default ProductPage;
