import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getAllProducts } from '../../utils/Api/ProductApi';
import './ProductList.css';
import ProductItem from "../Item/ProductItem";

const ProductList = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const productsData = await getAllProducts();
                setProducts(productsData);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };
        fetchProducts();
    }, []);

    return (
        <div className="product-list">
            {products.map(product => (
                <div key={product.productId} className="product-item">
                    <Link to={`/products/${product.productId}`} className="product-link">
                        <img src={product.imageUrls} alt={product.productName} className="product-image" />
                        <ProductItem productName={product.name} price={product.price} />
                    </Link>
                </div>
            ))}
        </div>
    );
};

export default ProductList;
