import React, { useState } from 'react';
import { addNewSong } from '../../Api/SongApi';
import { useNavigate } from 'react-router-dom';

const SongPage = () => {
    const [title, setTitle] = useState('');
    const [soundtrackFile, setSoundtrackFile] = useState(null);
    const [imageFile, setImageFile] = useState(null);
    const [genre, setGenre] = useState('');
    const [privacy, setPrivacy] = useState('public'); // Default privacy value

    const navigate = useNavigate(); // Get the navigate function

    const handleTitleChange = (event) => {
        setTitle(event.target.value);
    };

    const handleSoundtrackChange = (event) => {
        const file = event.target.files[0];
        setSoundtrackFile(file);
    };

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        setImageFile(file);
    };

    const handleGenreChange = (event) => {
        const selectedGenre = event.target.value;
        setGenre(selectedGenre);
    };

    const handlePrivacyChange = (event) => {
        const selectedPrivacy = event.target.value;
        setPrivacy(selectedPrivacy); // Set privacy directly to the selected option value
    };




    const addSong = async (event) => {
        event.preventDefault();

        const user = JSON.parse(localStorage.getItem('user') || '{}'); // Parse the stored string to an object
        if (!user || !user.Id) {
            console.error('User ID is missing');
            // Handle missing user ID appropriately, e.g., show an error message
            return;
        }

        try {
            const formData = new FormData();
            formData.append('userId', user.Id); // Assuming the ID is stored under user.Id
            formData.append('title', title);
            formData.append('soundtrackFile', soundtrackFile);
            formData.append('imageFile', imageFile);
            formData.append('genre', genre);
            formData.append('privacy', privacy);

            for (let [key, value] of formData.entries()) {
                console.log(`${key}:`, value);
            }
            await addNewSong(formData);
            navigate('/ListSong');
            window.location.reload();
        } catch (error) {
            console.error('Error:', error);
            // Optionally, display an error message to the user
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div style={{ width: '400px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)' }}>
                <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Add New Song</h2>
                <form onSubmit={addSong}>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Title:</label>
                        <input type="text" value={title} onChange={handleTitleChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Soundtrack File:</label>
                        <input type="file" onChange={handleSoundtrackChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Image File:</label>
                        <input type="file" onChange={handleImageChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Genre:</label>
                        <select value={genre} onChange={handleGenreChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}>
                            <option value="">Select Genre</option>
                            <option value="Workout">Workout</option>
                            <option value="Relaxation">Relaxation</option>
                            <option value="Party">Party</option>
                            <option value="StudyFocus">Study Focus</option>
                            <option value="MoodBooster">Mood Booster</option>
                            <option value="Chill">Chill</option>
                            <option value="Travel">Travel</option>
                            <option value="Morning">Morning</option>
                            <option value="Nighttime">Nighttime</option>
                            <option value="Throwback">Throwback</option>
                            <option value="LoveSongs">Love Songs</option>
                            <option value="SadSongs">Sad Songs</option>
                            <option value="Jazz">Jazz</option>
                            <option value="Classical">Classical</option>
                            <option value="Indie">Indie</option>
                            <option value="Rock">Rock</option>
                            <option value="Pop">Pop</option>
                            <option value="HipHopRap">Hip Hop/Rap</option>
                            <option value="Electronic">Electronic</option>
                            <option value="RandBSoul">R&B/Soul</option>
                        </select>
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Privacy:</label>
                        <select value={privacy} onChange={handlePrivacyChange} style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}>
                            <option value="public">Public</option>
                            <option value="private">Private</option>
                        </select>
                    </div>
                    <button type="submit" style={{ width: '100%', padding: '10px', borderRadius: '5px', backgroundColor: '#007bff', color: '#fff', border: 'none', cursor: 'pointer' }}>Add Song</button>
                </form>
            </div>
        </div>
    );

};

export default SongPage;
