import { useParams } from 'react-router-dom';
import { useEffect, useState } from "react";
import { getProductById } from "../../utils/Api/ProductApi";
import nextSvg from "./next.svg";
import backSvg from "./back.svg";

const ProductDetail = () => {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);
    const [mainImageIndex, setMainImageIndex] = useState(0); // State to keep track of the index of the main image
    const [startImageIndex, setStartImageIndex] = useState(0); // State to keep track of the starting index of the displayed images

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const productData = await getProductById(productId);
                setProduct(productData);
            } catch (error) {
                console.error('Error fetching product:', error);
            }
        };
        if (productId) {
            fetchProduct();
        }
    }, [productId]);

    if (!product) {
        return <div>Loading...</div>;
    }

    // Function to handle click on an image in the list and set it as the main image
    const handleImageClick = (index) => {
        setMainImageIndex(index);
    };

    // Function to handle next button click
    const handleNextClick = () => {
        if (mainImageIndex === product.imageUrls.length - 1) {
            setMainImageIndex(0); // Loop back to the first image
        } else {
            setMainImageIndex(mainImageIndex + 1);
        }
    };

    // Function to handle back button click
    const handleBackClick = () => {
        if (mainImageIndex === 0) {
            setMainImageIndex(product.imageUrls.length - 1); // Loop back to the last image
        } else {
            setMainImageIndex(mainImageIndex - 1);
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', maxWidth: '2000px', margin: '20px', gap: '20px', border: '1px solid #ccc' }}>
            {/* Left section */}
            <div style={{ flex: 1 }}>
                {/* Top section for the main image */}
                <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px', maxWidth: '600px', maxHeight: '600px', margin: 'auto', position: 'relative' }}>
                    {startImageIndex > 0 && <img src={backSvg} alt="Back" style={{ position: 'absolute', top: '50%', left: '0', transform: 'translateY(-50%)', width: '30px', height: '50px', cursor: 'pointer' }} onClick={handleBackClick} />}
                    <img src={product.imageUrls[mainImageIndex]} alt={product.name} style={{ maxWidth: '100%' }} />
                    {startImageIndex + 6 < product.imageUrls.length && <img src={nextSvg} alt="Next" style={{ position: 'absolute', top: '50%', right: '0', transform: 'translateY(-50%)', width: '30px', height: '50px', cursor: 'pointer' }} onClick={handleNextClick} />}
                </div>

                {/* Bottom section for the list of images */}
                <div style={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap' }}>
                    {product.imageUrls.map((imageUrl, index) => (
                        <img key={index} src={imageUrl} alt={product.name} style={{ width: '100px', margin: '5px', cursor: 'pointer' }} onClick={() => handleImageClick(index)} />
                    ))}
                </div>
            </div>

            {/* Right section */}
            <div style={{ flex: 1, marginLeft: '20px' }}>
                <h2>{product.name}</h2>
                <p>Description: {product.description}</p>
                <p>Price: {product.price}</p>
            </div>
        </div>
    );
};

export default ProductDetail;
