import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { updateSongById, deleteSongById } from '../../Api/SongApi';

const EditSongPage = () => {
    const { songId } = useParams();
    const [song, setSong] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchSongById = async () => {
            try {
                // Fetch song data by ID from your API
                const response = await fetch(`your-api-endpoint/${songId}`);
                const data = await response.json();
                setSong(data); // Update the song state with fetched data
            } catch (error) {
                console.error('Error fetching song:', error);
            }
        };

        fetchSongById(); // Call the fetch function
    }, [songId]);
    const handleUpdate = async (updatedSongData) => {
        try {
            await updateSongById(songId, updatedSongData);
            // Redirect to the song details page or any other page as needed
            navigate(`/song/${songId}`);
            window.location.reload();
        } catch (error) {
            console.error('Error updating the song:', error);
        }
    };

    const handleDelete = async () => {
        try {
            await deleteSongById(songId);
            // Redirect to the song list page or any other page as needed
            navigate('/ListSong');
        } catch (error) {
            console.error('Error deleting the song:', error);
        }
    };

    // Render the edit form with song data

    return (
        <div>
            {song && (
                <div>
                    <h2>Edit Song: {song.title}</h2>

                    <button onClick={handleDelete}>Delete Song</button>
                </div>
            )}
        </div>
    );
};

export default EditSongPage;
