import React from "react";
import previous from '../Icon/icons8-skip-to-start-30.png';
import pause from '../Icon/icons8-pause-50.png';
import play from '../Icon/icons8-play-50.png';
import next from '../Icon/icons8-forward-30.png';
import shuffle from '../Icon/icons8-shuffle-30.png';
import loop from    '../Icon/icons8-loop-30.png';
const AudioControls = ({onShuffle, isPlaying, onPlayPauseClick, onPrevClick, onNextClick, onLoop}) => (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <img src={shuffle} alt="shuffle" style={{ cursor: 'pointer' }} onClick={onShuffle}/>
        <img src={previous} alt="Previous" style={{ cursor: 'pointer' }} onClick={onPrevClick}/>
        {isPlaying ? (
            <img src={pause} alt="Pause" style={{ cursor: 'pointer' }} onClick={() => onPlayPauseClick(false)}/>
        ) : (
            <img src={play} alt="Play" style={{ cursor: 'pointer' }} onClick={onPlay => onPlayPauseClick(true)}/>
        )}
        <img src={next} alt="Next" style={{ cursor: 'pointer'}} onClick={onNextClick}/>
        <img src={loop} alt="loop" style={{ cursor: 'pointer' }} onClick={onLoop}/>
    </div>
);
export default AudioControls;
