import React, { useState, useEffect } from 'react';
import './FavoritePage.css';
import { getProductSizesByProductId } from '../../utils/Api/ProductSizeApi';
import {useCart} from "../Cart/CartContext";
const FavoritePage = () => {
    const [favorites, setFavorites] = useState(JSON.parse(localStorage.getItem('favorites')) || []);
    const [sizes, setSizes] = useState({});
    const [selections, setSelections] = useState({});
    const { addToCart } = useCart();

    useEffect(() => {
        favorites.forEach(async (product) => {
            const availableSizes = await getProductSizesByProductId(product.productId);
            setSizes((prevSizes) => ({ ...prevSizes, [product.productId]: availableSizes }));
        });
    }, [favorites]);

    const handleSizeChange = (productId, selectedSize) => {
        setSelections((prevSelections) => ({
            ...prevSelections,
            [productId]: { ...prevSelections[productId], size: selectedSize }
        }));
    };

    const handleQuantityChange = (productId, selectedQuantity) => {
        setSelections((prevSelections) => ({
            ...prevSelections,
            [productId]: { ...prevSelections[productId], quantity: selectedQuantity }
        }));
    };

    const handleAddToCart = (productId) => {
        const { size, quantity } = selections[productId];
        addToCart(productId, size, quantity);
    };

    const toggleFavorite = (product) => {
        const updatedFavorites = favorites.filter((favProduct) => favProduct.productId !== product.productId);
        setFavorites(updatedFavorites);
        localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
    };

    return (
        <div>
            <h1 className="favorites-h1">Favorites</h1>
            <div className="favorites-container">
                {favorites.map((product, index) => (
                    <div key={index} className="favorite-item">
                        {product.imageUrls && product.imageUrls.length > 0 && (
                            <img src={product.imageUrls[0]} alt={product.name} className="product-image" />
                        )}
                        <div className="product-info">
                            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                <p>{product.name}</p>
                                <button onClick={() => toggleFavorite(product)}>Remove</button>
                            </div>
                            <div style={{ display: 'flex', alignItems: 'center' }}>
                                <p>Price: {product.price} USD</p>
                                <select value={selections[product.productId]?.size || ''} onChange={(e) => handleSizeChange(product.productId, e.target.value)}>
                                    {sizes[product.productId]?.map((size, index) => (
                                        <option key={index} value={size}>{size}</option>
                                    ))}
                                </select>
                                <select value={selections[product.productId]?.quantity || 1} onChange={(e) => handleQuantityChange(product.productId, Number(e.target.value))}>
                                    {[...Array(10).keys()].map((num) => (
                                        <option key={num} value={num + 1}>{num + 1}</option>
                                    ))}
                                </select>
                                <button onClick={() => handleAddToCart(product.productId)}>Add to Cart</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default FavoritePage;
