import React, { useState } from 'react';
import { addNewSong } from '../api/songApi';

const SongPage = () => {
    const [title, setTitle] = useState('');
    const [soundtrackFile, setSoundtrackFile] = useState(null);
    const [imageFile, setImageFile] = useState(null);

    const handleTitleChange = (event) => {
        setTitle(event.target.value);
    };

    const handleSoundtrackChange = (event) => {
        setSoundtrackFile(event.target.files[0]);
    };

    const handleImageChange = (event) => {
        setImageFile(event.target.files[0]);
    };

    const addSong = async (event) => {
        event.preventDefault();
        try {
            const formData = new FormData();
            formData.append('title', title);
            formData.append('soundtrackFile', soundtrackFile);
            formData.append('imageFile', imageFile);

            await addNewSong(formData);
            // Optionally, you can navigate to a different page after adding the song
            // Example: history.push('/songs');
        } catch (error) {
            console.error('Error:', error);
            // Optionally, display an error message to the user
        }
    };

    return (
        <div>
            <h2>Add New Song</h2>
            <form onSubmit={addSong}>
                <div>
                    <label>Title:</label>
                    <input type="text" value={title} onChange={handleTitleChange} required />
                </div>
                <div>
                    <label>Soundtrack File:</label>
                    <input type="file" onChange={handleSoundtrackChange} required />
                </div>
                <div>
                    <label>Image File:</label>
                    <input type="file" onChange={handleImageChange} required />
                </div>
                <button type="submit">Add Song</button>
            </form>
        </div>
    );
};

export default SongPage;
