import { useParams } from 'react-router-dom';
import { useEffect, useState } from "react";
import { getProductById } from "../../utils/Api/ProductApi";
import nextSvg from "./next.svg";
import backSvg from "./back.svg";

const ProductDetail = () => {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);
    const [mainImageIndex, setMainImageIndex] = useState(0); // State to keep track of the index of the main image
    const [startImageIndex, setStartImageIndex] = useState(0); // State to keep track of the starting index of the displayed images

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const productData = await getProductById(productId);
                setProduct(productData);
            } catch (error) {
                console.error('Error fetching product:', error);
            }
        };
        if (productId) {
            fetchProduct();
        }
    }, [productId]);

    if (!product) {
        return <div>Loading...</div>;
    }

    // Function to handle click on an image in the list and set it as the main image
    const handleImageClick = (index) => {
        setMainImageIndex(index);
    };

    // Function to handle next in list button click
    const handleNextInListClick = () => {
        if (startImageIndex + 5 < product.imageUrls.length) {
            setStartImageIndex(startImageIndex + 5);
        }
    };

    // Function to handle back in list button click
    const handleBackInListClick = () => {
        if (startImageIndex > 0) {
            setStartImageIndex(startImageIndex - 5);
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', maxWidth: '2000px', margin: '20px', gap: '20px', border: '1px solid #ccc' }}>
            {/* Left section */}
            <div style={{ flex: 1 }}>
                {/* Top section for the main image */}
                <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px', maxWidth: '600px', maxHeight: '600px', margin: 'auto' }}>
                    <img src={product.imageUrls[mainImageIndex]} alt={product.name} style={{ maxWidth: '100%' }} />
                </div>
                {/* Bottom section for the list of images */}
                <div style={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap' }}>
                    {product.imageUrls.slice(startImageIndex, startImageIndex + 5).map((imageUrl, index) => {
                        const imageIndex = startImageIndex + index;
                        return (
                            <div key={index} style={{ position: 'relative', marginRight: '5px', marginBottom: '10px' }}>
                                {index === 0 && startImageIndex > 0 && (
                                    <img src={backSvg} alt="Back" style={{ position: 'absolute', left: '-15px', top: '50%', transform: 'translateY(-50%)', width: '30px', height: '50px', cursor: 'pointer' }} onClick={handleBackInListClick} />
                                )}
                                {index === 4 && (
                                    <img src={nextSvg} alt="Next" style={{ position: 'absolute', right: '-15px', top: '50%', transform: 'translateY(-50%)', width: '30px', height: '50px', cursor: 'pointer' }} onClick={handleNextInListClick} />
                                )}
                                {imageIndex < product.imageUrls.length ? (
                                    <img src={imageUrl} alt={product.name} style={{ width: '100px', cursor: 'pointer' }} onClick={() => handleImageClick(imageIndex)} />
                                ) : null}
                            </div>
                        );
                    })}
                </div>

            </div>

            {/* Right section */}
            <div style={{ flex: 1, marginLeft: '20px' }}>
                <h2>{product.name}</h2>
                <p>Description: {product.description}</p>
                <p>Price: {product.price}</p>
            </div>
        </div>
    );
};

export default ProductDetail;
