import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import { storage } from "../../firebase";
import defaultAlbumImage from '../../Img/default-cover-art.png';
import { getAllSongsByUserId } from '../../Api/SongApi';
import {addNewAlbum, addSongToAlbum, getAllSongsInAlbum, removeSongFromAlbum} from '../../Api/ArtistApi';
import { Guid } from 'guid-typescript';

const AddAlbumPage = () => {
    const [albumName, setAlbumName] = useState('');
    const [albumImage, setAlbumImage] = useState(null);
    const [imagePreview, setImagePreview] = useState(defaultAlbumImage);
    const [songs, setSongs] = useState([]);
    const [allUserSongs, setAllUserSongs] = useState([]);
    const [selectedSongs, setSelectedSongs] = useState(new Set());
    const [showAddSongModal, setShowAddSongModal] = useState(false);
    const navigate = useNavigate();
    const user = JSON.parse(localStorage.getItem('user') || '{}');

    useEffect(() => {
        loadUserSongs();
    }, [user.Id]);

    const loadUserSongs = async () => {
        const fetchedSongs = await getAllSongsByUserId(user.Id);
        setAllUserSongs(fetchedSongs.flat());
    };

    const handleSelectSong = (songId) => {
        const newSelectedSongs = new Set(selectedSongs);
        if (newSelectedSongs.has(songId)) {
            newSelectedSongs.delete(songId);
        } else {
            newSelectedSongs.add(songId);
        }
        setSelectedSongs(newSelectedSongs);
    };

    const handleSaveSelectedSongs = () => {
        setShowAddSongModal(false);
        setSongs(Array.from(selectedSongs)); // Update the list of selected songs
    };

    const handleCloseModal = () => {
        setShowAddSongModal(false);
    };

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setAlbumImage(file);
            const previewUrl = URL.createObjectURL(file);
            setImagePreview(previewUrl);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!albumName || !albumImage) {
            alert('Please provide album name and image.');
            return;
        }
        const albumId = Guid.create().toString(); // Generate a new GUID
        const songIdsArray = Array.from(selectedSongs);
        const imageFileRef = ref(storage, `AlbumImages/${albumId}`); // Use albumId as the image name
        const uploadResult = await uploadBytes(imageFileRef, albumImage);
        const imageURL = await getDownloadURL(uploadResult.ref);
        console.log('albumId: ',albumId)
        const albumData = {
            AlbumId: albumId,
            NameOfAlbum: albumName,
            ImageAlbumUrl: imageURL,
            SongIds: songIdsArray,
        };

        console.log(albumData)
        try {
            await addNewAlbum(albumData);
            // alert('Album added successfully!');
            // navigate('/ListAlbum');
        } catch (error) {
            console.error('Error adding album:', error);
            alert('Failed to add album.');
        }
    };

    return (
        <div>
            <h2>Add New Album</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="albumName">Album Name *</label>
                    <input
                        id="albumName"
                        type="text"
                        value={albumName}
                        onChange={(e) => setAlbumName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="albumImage">Album Image *</label>
                    <input
                        id="albumImage"
                        type="file"
                        accept="image/*"
                        onChange={handleImageChange}
                        required
                    />
                    <img src={imagePreview} alt="Album Preview" style={{ maxWidth: '200px' }} />
                </div>
                <div>
                    <h2>Songs in Album</h2>
                    {Array.from(selectedSongs).map(songId => {
                        const song = allUserSongs.find(s => s.songId === songId);
                        return song ? (
                            <div key={song.songId}>
                                {song.title}
                            </div>
                        ) : null;
                    })}
                    <button onClick={() => setShowAddSongModal(true)}>Add Songs</button>
                </div>
                {showAddSongModal && (
                    <div style={{ position: 'absolute', top: '20%', left: '30%', backgroundColor: 'white', padding: '20px', border: '1px solid black' }}>
                        <h2>Select Songs to Add</h2>
                        {allUserSongs.map((song) => (
                            <div key={song.songId}>
                                <input
                                    type="checkbox"
                                    checked={selectedSongs.has(song.songId)}
                                    onChange={() => handleSelectSong(song.songId)}
                                />
                                {song.title}
                            </div>
                        ))}
                        <button onClick={handleSaveSelectedSongs}>Save</button>
                        <button onClick={handleCloseModal}>Close</button>
                    </div>
                )}
                <button type="submit">Add Album</button>
            </form>
        </div>
    );
};

export default AddAlbumPage;
