import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
    getAlbumById,
    getAllSongsInAlbum,
    addSongToAlbum,
    deleteAlbum,
    removeSongFromAlbum,
} from '../../Api/ArtistApi';
import { getAllSongsByUserId } from '../../Api/SongApi'; // Import the function to fetch all songs by user ID

const EditAlbum = () => {
    const [album, setAlbum] = useState(null);
    const [songs, setSongs] = useState([]);
    const [allUserSongs, setAllUserSongs] = useState([]);
    const [selectedSongs, setSelectedSongs] = useState(new Set());
    const [showAddSongModal, setShowAddSongModal] = useState(false);
    const { albumId } = useParams();
    const navigate = useNavigate();
    const user = JSON.parse(localStorage.getItem('user') || '{}');

    useEffect(() => {
        loadAlbum();
        loadSongs();
        loadUserSongs();
    }, [albumId]);

    const loadAlbum = async () => {
        const fetchedAlbum = await getAlbumById(albumId);
        setAlbum(fetchedAlbum);
    };

    const loadSongs = async () => {
        const fetchedSongs = await getAllSongsInAlbum(albumId);
        setSongs(fetchedSongs);
    };

    const loadUserSongs = async () => {
        const fetchedSongs = await getAllSongsByUserId(user.Id);
        setAllUserSongs(fetchedSongs.flat()); // Assuming your API returns nested arrays of songs
    };

    const handleAddSongsToAlbum = async () => {
        for (let songId of selectedSongs) {
            await addSongToAlbum(albumId, { albumId, songId });
        }
        setShowAddSongModal(false); // Close modal after adding songs
        loadSongs(); // Refresh songs list in the album
    };

    const handleSelectSong = (songId) => {
        const newSelectedSongs = new Set(selectedSongs);
        if (newSelectedSongs.has(songId)) {
            newSelectedSongs.delete(songId);
        } else {
            newSelectedSongs.add(songId);
        }
        setSelectedSongs(newSelectedSongs);
    };

    const handleRemoveSong = async (songId) => {
        await removeSongFromAlbum(albumId, songId);
        loadSongs();
    };

    const handleDeleteAlbum = async () => {
        await deleteAlbum(albumId);
        navigate('/ListAlbum');
    };

    if (!album) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>Edit Album: {album.nameOfAlbum}</h1>
            <div>
                <h2>Songs in Album</h2>
                {Array.isArray(songs) && songs.map((song) => (
                    <div key={song.songId}>
                        {song.title}
                        <button onClick={() => handleRemoveSong(song.songId)}>Remove</button>
                    </div>
                ))}

                <button onClick={() => setShowAddSongModal(true)}>Add Songs</button>
            </div>
            <button onClick={handleDeleteAlbum}>Delete Album</button>

            {showAddSongModal && (
                <div style={{ position: 'absolute', top: '20%', left: '30%', backgroundColor: 'white', padding: '20px', border: '1px solid black' }}>
                    <h2>Select Songs to Add</h2>
                    {allUserSongs.map((song) => (
                        <div key={song.songId}>
                            <input
                                type="checkbox"
                                checked={selectedSongs.has(song.songId)}
                                onChange={() => handleSelectSong(song.songId)}
                            />
                            {song.title}
                        </div>
                    ))}
                    <button onClick={handleAddSongsToAlbum}>Add Selected Songs</button>
                    <button onClick={() => setShowAddSongModal(false)}>Close</button>
                </div>
            )}
        </div>
    );
};

export default EditAlbum;
