import { useParams } from 'react-router-dom';
import { useEffect, useState } from "react";
import { getProductById } from "../../utils/Api/ProductApi";
import nextSvg from "./next.svg";
import backSvg from "./back.svg";
import {getProductSizesByProductId} from "../../utils/Api/ProductSizeApi";

const ProductDetail = () => {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);
    const [productSizes, setProductSizes] = useState([]); // State to store product sizes
    const [mainImageIndex, setMainImageIndex] = useState(0);
    const [startImageIndex, setStartImageIndex] = useState(0);
    const [showSizeDropdown, setShowSizeDropdown] = useState(false);
    const [showQuantityDropdown, setShowQuantityDropdown] = useState(false);
    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const productData = await getProductById(productId);
                console.log('product data: ',productData)
                setProduct(productData);
            } catch (error) {
                console.error('Error fetching product:', error);
            }
        };
        const fetchProductSizes = async () => {
            try {
                const sizesData = await getProductSizesByProductId(productId);
                console.log('product size: ',sizesData)
                setProductSizes(sizesData);
            } catch (error) {
                console.error('Error fetching product sizes:', error);
            }
        };
        if (productId) {
            fetchProduct();
            fetchProductSizes(); // Fetch product sizes when productId changes
        }
    }, [productId]);

    if (!product) {
        return <div>Loading...</div>;
    }
    const handleImageClick = (index) => {
        setMainImageIndex(index);
    };
    const handleNextInListClick = () => {
        const newStartIndex = startImageIndex + 5;
        if (newStartIndex < product.imageUrls.length) {
            setStartImageIndex(Math.min(newStartIndex, product.imageUrls.length - 5));
        }
    };
    const handleBackInListClick = () => {
        const newStartIndex = Math.max(startImageIndex - 5, 0); // Ensure the new start index doesn't go below 0
        setStartImageIndex(newStartIndex);
        setMainImageIndex(newStartIndex); // Update main image index to correspond with the back button action
    };
    const toggleSizeDropdown = () => {
        setShowSizeDropdown(!showSizeDropdown);
    };
    const toggleQuantityDropdown = () => {
        setShowQuantityDropdown(!showQuantityDropdown);
    };



    return (
        <div style={{ display: 'flex', justifyContent: 'center', maxWidth: '2000px', margin: '20px', gap: '20px', border: '1px solid #ccc' }}>
            {/*left section*/}
            <div style={{ flex: 1 ,maxWidth:'50%', border: '1px solid #ccc',}}>
                <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '10px', maxWidth: '600px', maxHeight: '600px', margin: 'auto' }}>
                    <img src={product.imageUrls[mainImageIndex]} alt={product.name} style={{ maxWidth: '100%' }} />
                </div>
                <div style={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap' }}>
                    {product.imageUrls.slice(startImageIndex, Math.min(startImageIndex + 5, product.imageUrls.length)).map((imageUrl, index) => {
                        const imageIndex = startImageIndex + index;
                        return (
                            <div key={index} style={{ position: 'relative', marginRight: '5px', marginBottom: '10px' }}>
                                {index === 0 && startImageIndex > 0 && (
                                    <img src={backSvg} alt="Back" style={{ position: 'absolute', left: '-15px', top: '50%', transform: 'translateY(-50%)', width: '30px', height: '50px', cursor: 'pointer' }} onClick={handleBackInListClick} />
                                )}
                                {index === 4 && startImageIndex + 5 < product.imageUrls.length && (
                                    <img src={nextSvg} alt="Next" style={{ position: 'absolute', right: '-15px', top: '50%', transform: 'translateY(-50%)', width: '30px', height: '50px', cursor: 'pointer' }} onClick={handleNextInListClick} />
                                )}
                                <img src={imageUrl} alt={product.name} style={{ width: '100px', cursor: 'pointer' }} onClick={() => handleImageClick(imageIndex)} />
                            </div>
                        );
                    })}
                </div>
            </div>

            {/*right section*/}
            <div style={{ flex: 1, maxWidth: '50%', border: '1px solid #ccc', paddingLeft: '15px' }}>
                <h2 style={{ textAlign: 'left' }}>{product.name}</h2>
                <p style={{ textAlign: 'left', fontWeight: 'bold', color: '#F15E2C', fontSize: '1.5rem' }}>{product.price} VND</p>

                {/* Size dropdown */}
                <div>
                    <p style={{ textAlign: 'left', fontWeight: 'bold' }}>Size:</p>
                    <div style={{ position: 'relative' }}>
                        <span style={{ cursor: 'pointer' }} onClick={toggleSizeDropdown}>Select size ▼</span>
                        {showSizeDropdown && (
                            <div style={{ position: 'absolute', top: '100%', left: 0, backgroundColor: '#fff', border: '1px solid #ccc', zIndex: 999 }}>
                                {['35', '36', '36,5', '37', '38', '38,5', '39', '40', '40,5', '41', '42', '42,5', '43', '44', '44,5', '45', '46'].map((size, index) => (
                                    <div key={index} style={{ padding: '5px', cursor: 'pointer' }}>{size}</div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>

                {/* Quantity dropdown */}
                <div>
                    <p style={{ textAlign: 'left', fontWeight: 'bold' }}>Quantity:</p>
                    <div style={{ position: 'relative' }}>
                        <span style={{ cursor: 'pointer' }} onClick={toggleQuantityDropdown}>Select quantity ▼</span>
                        {showQuantityDropdown && (
                            <div style={{ position: 'absolute', top: '100%', left: 0, backgroundColor: '#fff', border: '1px solid #ccc', zIndex: 999 }}>
                                {[...Array(12).keys()].map((quantity) => (
                                    <div key={quantity + 1} style={{ padding: '5px', cursor: 'pointer' }}>{quantity + 1}</div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetail;
