import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { updateSongById, deleteSongById, getSongById } from '../../Api/SongApi';

const EditSong = () => {
    const { songId } = useParams();
    console.log(`Received songId in EditSong: ${songId}`);
    const [song, setSong] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchSong = async () => {
            try {
                const songData = await getSongById(songId);
                setSong(songData);
            } catch (error) {
                console.error('Error fetching song:', error);
            }
        };

        fetchSong();
    }, [songId]);

    const handleFileChange = (event) => {
        const { name, files } = event.target;
        setSong({ ...song, [name]: files[0] });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('title', song.title);
        if (song.image) formData.append('image', song.image);
        if (song.soundTrack) formData.append('soundTrack', song.soundTrack);

        try {
            await updateSongById(songId, formData);
            alert('Song updated successfully');
            navigate('/ListSong'); // Navigate back to the list of songs
        } catch (error) {
            console.error('Error updating the song:', error);
        }
    };

    const handleDelete = async () => {
        const confirmation = window.confirm('Are you sure you want to delete this song?');
        if (confirmation) {
            try {
                await deleteSongById(songId);
                alert('Song deleted successfully');
                navigate('/ListSong'); // Navigate back to the list of songs
            } catch (error) {
                console.error('Error deleting the song:', error);
            }
        }
    };

    return (
        <div className="edit-song-page">
            <h2>Edit Song</h2>
            <form onSubmit={handleSubmit} className="edit-song-form">
                <div className="song-media">
                    <div className="image-preview">
                        {song && song.imageData && (
                            <img src={`data:image/png;base64,${song.imageData}`} alt="Cover art" style={{ width: '150px', height: '150px' }} />
                        )}
                        <input type="file" name="image" onChange={handleFileChange} />
                    </div>
                    <div className="soundtrack-preview">
                        {song && song.soundTrackData && (
                            <audio controls src={`data:audio/mpeg;base64,${song.soundTrackData}`}></audio>
                        )}
                        <input type="file" name="soundTrack" onChange={handleFileChange} />
                    </div>
                    <button type="submit" className="btn save-changes">Save Changes</button>
                    <button type="button" onClick={handleDelete} className="btn delete-song">Delete Song</button>
                </div>

            </form>
        </div>
    );
};

export default EditSong;
