import { useParams } from 'react-router-dom';
import {useEffect, useState} from "react";
import {getProductById} from "../../utils/Api/ProductApi";

const ProductDetail = () => {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const productData = await getProductById(productId);
                console.log('product detail: ',productData)
                setProduct(productData);
            } catch (error) {
                console.error('Error fetching product:', error);
            }
        };
        if (productId) {
            fetchProduct();
        }
    }, [productId]);

    if (!product) {
        return <div>Loading...</div>;
    }

    return (
        <div style={{ display: 'flex', justifyContent: 'center', maxWidth: '2000px', margin: '20px', gap: '20px', border: '1px solid #ccc' }}>
            {/* Left section */}
            <div style={{ flex: 1 }}>
                {/* Top section for the main image */}
                <div style={{display: 'flex', justifyContent: 'center', marginBottom: '20px',maxWidth:'600px',maxHeight:'600px' }}>
                    <img src={product.imageUrls[0]} alt={product.name} style={{ maxWidth: '100%' }} />
                </div>
                {/* Bottom section for the list of images */}
                <div style={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap' }}>
                    {product.imageUrls.slice(1).map((imageUrl, index) => (
                        <img key={index} src={imageUrl} alt={product.name} style={{ width: '100px', margin: '5px' }} />
                    ))}
                </div>
            </div>


            {/*right section*/}
            <div style={{ flex: 1, marginLeft: '20px' }}>
                <h2>{product.name}</h2>
                <p>Description: {product.description}</p>
                <p>Price: {product.price}</p>
            </div>
        </div>
    );
};

export default ProductDetail;
