import React, { useEffect, useState } from 'react';
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import { storage } from "../../firebase";
import { addProduct, GetAllProductNamesAndIds,getProductById } from "../../utils/Api/ProductApi";
import { addProductSize } from "../../utils/Api/ProductSizeApi";
import { Guid } from "guid-typescript";

const ProductPage = () => {

    const [image, setImage] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [design, setDesign] = useState(0);
    const [productLine, setProductLine] = useState(0);
    const [material, setMaterial] = useState(0);
    const [color, setColor] = useState(0);
    const [products, setProducts] = useState([]);
    const [sizes, setSizes] = useState([]);
    const [sizeAddedMessage, setSizeAddedMessage] = useState('');
    const [newSize, setNewSize] = useState('');
    const [newQuantity, setNewQuantity] = useState('');
    const [selectedProduct, setSelectedProduct] = useState(null); // State to hold the selected product details

    // Arrays for dropdown options
    const Design = ['LowTop', 'HighTop', 'Mule'];
    const ProductLine = ['Basas', 'Vintas', 'Urbas', 'Pattas', 'Socks', 'Shoelaces'];
    const Material = ['Canvas', 'Suede', 'Leather', 'Flannel', 'Corduroy'];
    const Color = ['Gray', 'White', 'Cream', 'Charcoal', 'Teal', 'Beige', 'Silver', 'NavyBlue', 'OliveGreen', 'Lavender', 'Coral', 'CarrotOrange', 'CrimsonRed'];
    const Size = ['35', '36', '36,5', '37', '38', '38,5', '39', '40', '40,5', '41', '42', '42,5', '43', '44', '44,5', '45', '46'];

    // Maps for converting dropdown values to numbers
    const designMap = { LowTop: 0, HighTop: 1, Mule: 2 };
    const productLineMap = { Basas: 0, Vintas: 1, Urbas: 2, Pattas: 3, Socks: 4, Shoelaces: 5 };
    const materialMap = { Canvas: 0, Suede: 1, Leather: 2, Flannel: 3, Corduroy: 4 };
    const colorMap = {Gray: 0, White: 1, Cream: 2, Charcoal: 3, Teal: 4, Beige: 5, Silver: 6, NavyBlue: 7, OliveGreen: 8, Lavender: 9, Coral: 10, CarrotOrange: 11, CrimsonRed: 12};

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const productsData = await GetAllProductNamesAndIds();
                console.log('product data list: ',productsData)
                setProducts(productsData);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);
    // Inside handleProductClick function
    const handleProductClick = async (productId) => {
        try {
            const productData = await getProductById(productId);
            console.log('Product details:', productData);

            // Update state hooks with product details
            setName(productData.name);
            setDescription(productData.description);
            setPrice(productData.price.toString());
            setDesign(Design[productData.design]);
            setProductLine(ProductLine[productData.productLine]);
            setMaterial(Material[productData.material]);
            setColor(Color[productData.color]);

            const imageUrl = productData.imageUrls;
            setImagePreview(imageUrl);
            console.log('Image Preview URL:', imagePreview);
            setSelectedProduct(productData);
        } catch (error) {
            console.error('Error fetching product details:', error);
        }
    };

    // Function to handle adding a new size
    const handleAddNewSize = () => {
        if (newSize && newQuantity) {
            const newSizeObject = { size: newSize, quantity: parseInt(newQuantity), addQuantity: '' };
            const updatedSizes = [...sizes, newSizeObject];
            setSizes(updatedSizes);
            setNewSize('');
            setNewQuantity('');
        } else {
            console.error('Please provide both size and quantity');
        }
    };
    // Function to handle changes in size input fields
    const handleSizeChange = (index, field, value) => {
        const updatedSizes = [...sizes];
        if (field === 'addQuantity') {
            updatedSizes[index][field] = isNaN(value) ? '' : parseInt(value);
        } else if (field === 'size') {
            const sanitizedValue = value.replace(',', '');
            updatedSizes[index][field] = sanitizedValue;
        } else {
            updatedSizes[index][field] = value;
        }
        setSizes(updatedSizes);
    };
    // Function to handle image upload
    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        if (file) {
            setImage(file);
            setImagePreview(URL.createObjectURL(file));
        }
    };
    // Function to save product data
    const handleSaveProduct = async () => {
        if (!image) {
            console.error('No image to upload');
            return;
        }

        const newProductId = Guid.create().toString();
        const imageRef = ref(storage, `products/${newProductId}/${image.name}`);

        try {
            const snapshot = await uploadBytes(imageRef, image);
            const imageUrl = await getDownloadURL(snapshot.ref);

            const numericDesign = designMap[design];
            const numericProductLine = productLineMap[productLine];
            const numericMaterial = materialMap[material];
            const numericColor = colorMap[color];

            const updatedSizes = sizes.map(size => {
                const sizeValue = size.size;
                const sizeIndex = Size.indexOf(sizeValue);
                if (sizeValue && sizeIndex !== -1) {
                    return {
                        size: sizeIndex,
                        quantity: (parseInt(size.quantity) || 0) + (parseInt(size.addQuantity) || 0),
                        productSizeId: `${sizeValue}-${newProductId}`
                    };
                } else {
                    console.error('Size value is undefined or not found in Size array:', sizeValue);
                    return null;
                }
            }).filter(size => size !== null);

            const productToSave = {
                name,
                description,
                design: numericDesign,
                productLine: numericProductLine,
                material: numericMaterial,
                color: numericColor,
                price: parseFloat(price),
                sizes: updatedSizes.map(size => ({
                    ...size,
                    productSizeId: `${Size[size.size]}-${newProductId}`,
                })),
                productId:newProductId,
                ImageUrls: imageUrl,
            };

            console.log('product: ', productToSave);

            const response = await addProduct(productToSave);
            console.log('Product added successfully:', response);

            await Promise.all(updatedSizes.map(async (size) => {
                const productSizeData = {
                    ProductSizeId: size.productSizeId,
                    Size: size.size,
                    Quantity: size.quantity,
                    ProductId: newProductId
                };
                console.log('product size data',productSizeData)
                await addProductSize(productSizeData);
            }));

            console.log('Product sizes added successfully');
        } catch (error) {
            console.error('Error saving product:', error);
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', maxWidth: '2000px', margin: '20px auto', gap: '20px', border: '1px solid #ccc' }}>
            {/* Product List */}
            <div style={{ width: '20%', background: 'white', padding: '20px', border: '1px solid #ccc' }}>
                <h1>Product List</h1>
                <table style={{ width: '100%', borderCollapse: 'collapse', border: '1px solid #ccc' }}>
                    <thead>
                    <tr style={{ backgroundColor: '#f2f2f2' }}>
                        <th style={{ padding: '5px', textAlign: 'left' }}>Name</th>
                    </tr>
                    </thead>
                    <tbody>
                    {products.map(product => (
                        <tr key={product.id} onClick={() => handleProductClick(product.id)}> {/* Pass product.id instead of product.productId */}
                            <td key={product.id} style={{ padding: '5px', textAlign: 'left', cursor: 'pointer' }}>{product.name}</td>
                        </tr>
                    ))}

                    </tbody>
                </table>
            </div>

            {/* Product Details */}
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', width: '80%', background: 'white', padding: '10px', boxSizing: 'border-box', border: '1px solid #ccc' }}>
                {/* Left Section */}
                <div style={{ width: '80%', background: 'white', padding: '20px', boxSizing: 'border-box', border: '1px solid #ccc' }}>
                    <div style={{ display: 'flex', paddingBottom: '10px', marginBottom: '20px', border: '1px solid #ccc', alignItems: 'flex-start' }}>
                        <input type="file" accept="image/*" onChange={handleImageUpload} style={{ display: 'none' }} id="imageUpload" />
                        <label htmlFor="imageUpload" style={{ width: '30%', marginRight: '5px' }}>
                            {imagePreview && (
                                <div style={{ width: '300px', height: '300px', border: '1px solid #ccc' }}>
                                    <img src={imagePreview} alt="Preview" style={{ width: '300px', height: '300px' }} />
                                </div>
                            )}
                            {!imagePreview && (
                                <div style={{ width: '300px', height: '300px', border: '1px solid #ccc', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                                    <span>No Image</span>
                                </div>
                            )}
                        </label>
                        {/* Product Information */}
                        <div style={{ display: 'flex', flexDirection: 'column', width: '70%', height: '300px', border: '1px solid #ccc', marginBottom: '5px', marginRight: '5px', boxSizing: 'border-box' }}>
                            <div style={{ height: '20%' }}>
                                <h3 style={{ marginBottom: '2px' }}>Name:</h3>
                                <input type="text" value={name} onChange={(e) => setName(e.target.value)} style={{ width: '100%', height: '50%', boxSizing: 'border-box' }} />
                            </div>
                            <div style={{ height: '55%' }}>
                                <h3 style={{ marginBottom: '2px' }}>Description:</h3>
                                <textarea value={description} onChange={(e) => setDescription(e.target.value)} style={{ width: '100%', height: '80%', boxSizing: 'border-box' }} />
                            </div>
                            <div style={{ height: '20%' }}>
                                <h3 style={{ marginBottom: '2px' }}>Price:</h3>
                                <input type="text" value={price} onChange={(e) => setPrice(e.target.value)} style={{ width: '40%', height: '50%', boxSizing: 'border-box' }} />
                            </div>
                        </div>
                    </div>

                    {/* Product size */}
                    <div style={{ display: 'flex', width: '800px', justifyContent: 'space-between', alignItems: 'flex-start', padding: '5px' }}>
                        <div style={{ flex: 1, marginRight: '10px', overflowY: 'auto', maxHeight: '400px' }}>
                            <table style={{ width: '100%', borderCollapse: 'collapse', border: '1px solid #ccc' }}>
                                <thead>
                                <tr style={{ backgroundColor: '#f2f2f2' }}>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Size</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Quantity</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Add Quantity</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Actions</th>
                                </tr>
                                </thead>
                                <tbody>
                                {sizes.length === 0 ? (
                                    <tr>
                                        <td colSpan="4" style={{ textAlign: 'center', padding: '10px' }}>No sizes added yet</td>
                                    </tr>
                                ) : (
                                    sizes.map((size, index) => (
                                        <tr key={index} style={{ backgroundColor: 'white', border: '1px solid #ccc' }}>
                                            <td style={{ padding: '10px', textAlign: 'left' }}>{size.size}</td>
                                            <td style={{ padding: '10px', textAlign: 'left' }}>{size.quantity}</td>
                                            <td style={{ padding: '10px', textAlign: 'left' }}>
                                                <input type="number" value={size.addQuantity} onChange={(e) => handleSizeChange(index, 'addQuantity', e.target.value)} />
                                            </td>
                                            <td style={{ padding: '5px', textAlign: 'left' }}>
                                                <button onClick={() => handleAddNewSize(index)}>Add</button>
                                            </td>
                                        </tr>
                                    ))
                                )}
                                <tr style={{ backgroundColor: 'white', border: '1px solid #ccc' }}>
                                    <td>
                                        <select value={newSize} onChange={(e) => setNewSize(e.target.value)}>
                                            <option value="">Size</option>
                                            {Size.map((size, index) => (
                                                <option key={index} value={size}>{size}</option>
                                            ))}
                                        </select>
                                    </td>
                                    <td>
                                        <input type="text" value={newQuantity} onChange={(e) => setNewQuantity(e.target.value)} />
                                    </td>
                                    <td>
                                        <button onClick={handleAddNewSize}>Add</button>
                                    </td>
                                </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>

                </div>
                {/* Right Section */}
                <div style={{ width: '20%', background: 'white', padding: '20px', boxSizing: 'border-box', border: '1px solid #ccc' }}>
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Design</h3>
                        <select style={{ width: '100%' }} value={design} onChange={(e) => setDesign(e.target.value)}>
                            {Design.map((designOption, index) => (
                                <option key={designOption} value={designOption}>{designOption}</option>
                            ))}
                        </select>

                    </div>
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Product Line</h3>
                        <select style={{ width: '100%' }} value={productLine} onChange={(e) => setProductLine(e.target.value)}>
                            {ProductLine.map((productLine, index) => (
                                <option key={index} value={productLine}>{productLine}</option>
                            ))}
                        </select>
                    </div>
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Material</h3>
                        <select style={{ width: '100%' }} value={material} onChange={(e) => setMaterial(e.target.value)}>
                            {Material.map((material, index) => (
                                <option key={index} value={material}>{material}</option>
                            ))}
                        </select>
                    </div>
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Color</h3>
                        <select style={{ width: '100%' }} value={color} onChange={(e) => setColor(e.target.value)}>
                            {Color.map((color, index) => (
                                <option key={index} value={color}>{color}</option>
                            ))}
                        </select>
                    </div>
                    <button onClick={handleSaveProduct} style={{ backgroundColor: 'blue', color: 'white', padding: '15px', fontSize: '20px', fontWeight: 'bold' }}>Add</button>
                    <button style={{ backgroundColor: 'red', color: 'white', padding: '15px', fontSize: '20px', fontWeight: 'bold' }}>Delete</button>
                    <button onClick={handleSaveProduct} style={{ backgroundColor: 'green', color: 'white', padding: '15px', fontSize: '20px', fontWeight: 'bold' }}>Save</button>                </div>
            </div>
        </div>
    );
};

export default ProductPage;
