import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import { storage } from "../../firebase";
import defaultAlbumImage from '../../Img/default-cover-art.png';
import { getAllSongsByUserId, addNewSong } from '../../Api/SongApi';
import { addNewAlbum } from '../../Api/ArtistApi';

const AddAlbumPage = () => {
    const [albumName, setAlbumName] = useState('');
    const [albumImage, setAlbumImage] = useState(null);
    const [imagePreview, setImagePreview] = useState(defaultAlbumImage);
    const [selectedSongIds, setSelectedSongIds] = useState([]);
    const [allUserSongs, setAllUserSongs] = useState([]);
    const navigate = useNavigate();
    const [showSongSelector, setShowSongSelector] = useState(false);

    const user = JSON.parse(localStorage.getItem('user') || '{}'); // Replace with the actual user ID

    useEffect(() => {
        loadUserSongs();
    }, []);

    const loadUserSongs = async () => {
        try {
            const userSongs = await getAllSongsByUserId(user.Id);
            setAllUserSongs(userSongs);
        } catch (error) {
            console.error('Error loading user songs:', error);
        }
    };

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setAlbumImage(file);
            const previewUrl = URL.createObjectURL(file);
            setImagePreview(previewUrl);
        }
    };
    const toggleSongSelector = () => setShowSongSelector(!showSongSelector);

    const handleSongSelection = (songId) => {
        setSelectedSongIds(prevIds =>
            prevIds.includes(songId)
                ? prevIds.filter(id => id !== songId)
                : [...prevIds, songId]
        );
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!albumName || !albumImage || selectedSongIds.length === 0) {
            alert('Please provide album name, image, and select at least one song.');
            return;
        }

        const imageFileRef = ref(storage, `AlbumImages/${albumImage.name}`);
        const uploadResult = await uploadBytes(imageFileRef, albumImage);
        const imageURL = await getDownloadURL(uploadResult.ref);

        const albumData = {
            NameOfAlbum: albumName,
            ImageAlbumUrl: imageURL,
            SongIds: selectedSongIds
        };

        try {
            const result = await addNewAlbum(albumData);
            console.log(result);
            alert('Album added successfully!');
            navigate('/ListAlbum');
        } catch (error) {
            console.error('Error adding album:', error);
            alert('Failed to add album.');
        }
    };

    return (
        <div>
            <h2>Add New Album</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="albumName">Album Name *</label>
                    <input
                        id="albumName"
                        type="text"
                        value={albumName}
                        onChange={(e) => setAlbumName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="albumImage">Album Image *</label>
                    <input
                        id="albumImage"
                        type="file"
                        accept="image/*"
                        onChange={handleImageChange}
                        required
                    />
                    <img src={imagePreview} alt="Album Preview" style={{ maxWidth: '200px' }} />
                </div>
                <div>
                    <h3>Select Songs for Album</h3>
                    {allUserSongs.map(song => (
                        <div key={song.songId}>
                            <label>
                                <input
                                    type="checkbox"
                                    value={song.songId}
                                    checked={selectedSongIds.includes(song.songId)}
                                    onChange={() => handleSongSelection(song.songId)}
                                />
                                {song.title}
                            </label>
                        </div>
                    ))}
                </div>
                <button type="submit">Add Album</button>
            </form>
        </div>
    );
};

export default AddAlbumPage;
