import React, { useState, useEffect } from 'react';
import axios from 'axios';
import defaultImage from '../../Img/DefaultAvatar.png';

const ProductPage = () => {
    const [products, setProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('/api/products');
            setProducts(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleSelectProduct = (product) => {
        setSelectedProduct(product);
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            {/* Product List on the Left */}
            <div style={{ width: '50%', borderRight: '1px solid #ccc', padding: '20px' }}>
                <h1>Products</h1>
                <ul style={{ listStyle: 'none', padding: 0 }}>
                    {products.map((product) => (
                        <li key={product.productId} style={{ cursor: 'pointer', marginBottom: '10px' }} onClick={() => handleSelectProduct(product)}>
                            {product.name} - ${product.price}
                        </li>
                    ))}
                </ul>
            </div>

            {/* Product Details on the Right */}
            <div style={{ width: '50%', padding: '20px' }}>
                {selectedProduct ? (
                    <div>
                        <h2>{selectedProduct.name}</h2>
                        <img src={defaultImage} alt="Product" style={{ maxWidth: '100%', marginBottom: '20px' }} />
                        <p>Description: {selectedProduct.description}</p>
                        <p>Price: ${selectedProduct.price}</p>
                        {/* Add more product details as needed */}
                    </div>
                ) : (
                    <p>No product selected</p>
                )}
            </div>
        </div>
    );
};

export default ProductPage;
