import React, { useState, useEffect } from 'react';
import { getOrderDetail } from '../../utils/Api/OrderApi';
import { useParams } from 'react-router-dom';
import {getProductById} from "../../utils/Api/ProductApi";
import {confirmOrderItem} from '../../utils/Api/OrderItemApi'
import checkOrderItemIcon from '../../Img/checkOrderItem.svg';
import './OrderDetailPage.css';
const OrderDetailPage = () => {
    const { orderId } = useParams();
    const [orderDetail, setOrderDetail] = useState(null);
    const [productDetails, setProductDetails] = useState({});
    const [confirmedItems, setConfirmedItems] = useState({});
    const orderStatusMapping = [
        "Pending",
        "Processing",
        "Shipped",
        "Delivered",
        "Cancelled"
    ];
    useEffect(() => {
        if (orderId) {
            fetchOrderDetail(orderId);
        }
    }, [orderId]);

    const fetchOrderDetail = async (orderId) => {
        try {
            const orderDetailData = await getOrderDetail(orderId);
            setOrderDetail(orderDetailData);

            // Fetch product details for each order item
            const productDetailPromises = orderDetailData.orderItems.map(item =>
                getProductById(item.productId)
            );
            const productDetailsData = await Promise.all(productDetailPromises);
            console.log('productDetailsData return: ',productDetailsData)
            setProductDetails(productDetailsData);
        } catch (error) {
            console.error('Error fetching order detail:', error);
        }
    };
    const handleDoneClick = async (orderId) => {
        try {
            const response= await confirmOrderItem(orderId);
            console.log('Response data:', response.data);
            if (response.message === "confirm order item") {
                setConfirmedItems(prev => ({ ...prev, [orderId]: true })); // Mark as confirmed
            }
        } catch (error) {
            console.error('Error confirming order item:', error);
            // Handle the error appropriately, e.g., show an error message
        }
    };
    if (!orderDetail) {
        return <p>Loading...</p>;
    }

    return (
        <div className="order-detail-container">
            <h1>Order Detail</h1>
            <div className="order-info"> {/* Row for order details */}
                <p><strong>Email:</strong> {orderDetail.email}</p>
                <p><strong>Total Price:</strong> ${orderDetail.totalPrice.toFixed(2)}</p>
                <p><strong>Date:</strong> {new Date(orderDetail.dateTime).toLocaleString()}</p>
                <p><strong>Phone Number:</strong> {orderDetail.phoneNumber}</p>
                <p><strong>Status:</strong> {orderStatusMapping[orderDetail.status]}</p>
            </div>

            <h2>Order Items</h2>
            <div className="order-items">
                {productDetails.length > 0 && productDetails.map((product, index) => {
                    const orderItem = orderDetail.orderItems.find(
                        (item) => item.productId === product.productId
                    );
                    return (
                        <div key={index} className="order-item">
                            <div className="product-image">
                                <img src={product.imageUrls[0]} alt={product.name} style={{
                                    width: '200px',
                                    height: '200px',
                                    objectFit: 'cover',
                                    borderRadius: '8px'
                                }}/>
                            </div>
                            <div className="product-info">
                                <p><strong>Product Name:</strong> {product.name}</p>
                                <p><strong>Price:</strong> ${product.price.toFixed(2)}</p>
                                <strong>Quantity:</strong> {orderItem.quantity}
                                <button onClick={() => handleDoneClick(orderItem.orderItemId)}>
                                    Done
                                </button>
                                {confirmedItems[orderItem.orderItemId] && <img src={checkOrderItemIcon} alt="Confirmed" />}

                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default OrderDetailPage;
