import React, { useState, useEffect, useRef } from "react";
import AudioControls from "./AudioControls";
import volumeIcon from '../Icon/icons8-volume-50.png';
import moreoption from '../Icon/icons8-more-50.png'
const AudioPlayer = ({ tracks }) => {
    // State
    const [trackIndex, setTrackIndex] = useState(0);
    const [trackProgress, setTrackProgress] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);

    // Destructure for conciseness
    const { title, artist, audioSrc,image } = tracks[trackIndex];

    // Refs
    const audioRef = useRef(new Audio(audioSrc));
    const intervalRef = useRef();
    const isReady = useRef(false);

    // Destructure for conciseness
    const { duration } = audioRef.current;

    const currentPercentage = duration
        ? `${(trackProgress / duration) * 100}%`
        : "0%";
    const trackStyling = `
    -webkit-gradient(linear, 0% 0%, 100% 0%, color-stop(${currentPercentage}, #fff), color-stop(${currentPercentage}, #777))
  `;

    const startTimer = () => {
        // Clear any timers already running
        clearInterval(intervalRef.current);

        intervalRef.current = setInterval(() => {
            if (audioRef.current.ended) {
                toNextTrack();
            } else {
                setTrackProgress(audioRef.current.currentTime);
            }
        }, [1000]);
    };

    const onScrub = (value) => {
        // Clear any timers already running
        clearInterval(intervalRef.current);
        audioRef.current.currentTime = value;
        setTrackProgress(audioRef.current.currentTime);
    };

    const onScrubEnd = () => {
        // If not already playing, start
        if (!isPlaying) {
            setIsPlaying(true);
        }
        startTimer();
    };

    const toPrevTrack = () => {
        if (trackIndex - 1 < 0) {
            setTrackIndex(tracks.length - 1);
        } else {
            setTrackIndex(trackIndex - 1);
        }
    };

    const toNextTrack = () => {
        if (trackIndex < tracks.length - 1) {
            setTrackIndex(trackIndex + 1);
        } else {
            setTrackIndex(0);
        }
    };

    useEffect(() => {
        if (isPlaying) {
            audioRef.current.play();
            startTimer();
        } else {
            audioRef.current.pause();
        }
    }, [isPlaying]);

    // Handles cleanup and setup when changing tracks
    useEffect(() => {
        audioRef.current.pause();

        audioRef.current = new Audio(audioSrc);
        setTrackProgress(audioRef.current.currentTime);

        if (isReady.current) {
            audioRef.current.play();
            setIsPlaying(true);
            startTimer();
        } else {
            // Set the isReady ref as true for the next pass
            isReady.current = true;
        }
    }, [trackIndex]);

    useEffect(() => {
        // Pause and clean up on unmount
        return () => {
            audioRef.current.pause();
            clearInterval(intervalRef.current);
        };
    }, []);


    // const playerContainerStyle = {
    //     display: 'flex',
    //     justifyContent: 'space-between',
    //     alignItems: 'center',
    //     backgroundColor: '#000000',
    //     color: '#ffffff',
    //     position: 'fixed',
    //     bottom: '0',
    //     left: '0',
    //     right: '0',
    //     width: '100%',
    // };
    // const leftSectionStyle = {
    //     display: 'flex',
    //     alignItems: 'center',
    //     justifyContent: 'flex-start',
    //     color: '#ffffff',
    //     marginRight:'5px',
    //     width: '350px'
    // };
    // const centerSectionStyle = {
    //     flex: '1',
    //     display: 'flex',
    //     flexDirection: 'column',
    //     alignItems: 'center',
    //     padding: '5px',
    //     marginRight:'5px'
    // };
    //
    // const rightSectionStyle = {
    //     display: 'flex',
    //     justifyContent: 'flex-end',
    //     alignItems: 'center',
    //     marginRight: '15px',
    //     width: '150px',
    // };
    //
    //
    // const progressBarStyle = {
    //     width: '100%', // Allow the progress bar to fill the width of the center section
    //     // marginBottom: '1px', // Space between progress bar and control icons
    //     // marginTop:'5px'
    // };

    const playerContainerStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        backgroundColor: '#000000',
        color: '#ffffff',
        position: 'fixed',
        bottom: '0',
        left: '0',
        right: '0',
        width: '100vw', // This ensures it spans the full width of the viewport
        padding: '1rem', // Add padding to avoid sticking to the screen edges
    };

    const leftSectionStyle = {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-start',
    };

    const centerSectionStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        flex: 1, // This allows the center section to grow and take available space
    };

    const rightSectionStyle = {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end', // This pushes the volume and more options to the right
    };

    const progressBarStyle = {
        width: '100%',
        margin: '0.5rem 0', 
    };


    return (
        <div className="audio-player" style={playerContainerStyle}>
            <div style={leftSectionStyle}>
                <img
                    src={tracks[trackIndex].image}
                    alt={`Track artwork for ${tracks[trackIndex].title} by ${tracks[trackIndex].artist}`}
                    style={{ width: '90px', height: '90px', borderRadius: '90px',margin:'1px' }}
                />
                <div >
                    <h2 >{tracks[trackIndex].title}</h2>
                    <p >{tracks[trackIndex].artist}</p>
                </div>

            </div>

            <div style={centerSectionStyle}>
                <AudioControls
                    isPlaying={isPlaying}
                    onPrevClick={toPrevTrack}
                    onNextClick={toNextTrack}
                    onPlayPauseClick={setIsPlaying}
                />
                <input
                    type="range"
                    value={trackProgress}
                    step="1"
                    min="0"
                    max={duration || 0}
                    className="progress"
                    onChange={(e) => onScrub(e.target.value)}
                    onMouseUp={onScrubEnd}
                    onKeyUp={onScrubEnd}
                    style={progressBarStyle}
                />
            </div>

            <div style={rightSectionStyle}>
                <img src={volumeIcon} alt="Volume control" />
                <img src={moreoption} alt="More options" />
            </div>
        </div>
    );
};

export default AudioPlayer;
