import React, { useState, useEffect, useRef } from 'react';
import AudioControls from './AudioControls'; // Update the import path as necessary

const AudioPlayer = ({ track }) => {
    const audioRef = useRef(new Audio());
    const [isPlaying, setIsPlaying] = useState(false);

    // Load new track when the 'track' prop changes
    useEffect(() => {
        // Make sure the track is defined and has an 'audioSrc' property
        if (track && track.audioSrc) {
            audioRef.current.src = track.audioSrc;
            audioRef.current.load();
        }
    }, [track]);

    // Play or pause the audio when 'isPlaying' changes
    useEffect(() => {
        if (isPlaying) {
            audioRef.current.play().catch(error => console.error('Error playing the stream:', error));
        } else {
            audioRef.current.pause();
        }
    }, [isPlaying]);

    // Only render if track is defined
    if (!track || !track.audioSrc) {
        console.log("track is null")
        return null;
    }
    if (isLoading) {
        // Render a loading indicator until the data is fetched
        return <div>Loading...</div>;
    }
    return (
        <footer style={{ width: '100%', position: 'fixed', bottom: 0, backgroundColor: '#fff', boxShadow: '0 -2px 5px rgba(0, 0, 0, 0.2)' }}>
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '1rem' }}>
                {track && (
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                        <img src={track.image} alt={track.title} style={{ width: '50px', height: '50px', marginRight: '1rem' }} />
                        <div>
                            <h3 style={{ marginBottom: '0.5rem' }}>{track.title}</h3>
                            <p style={{ margin: 0 }}>{track.artist}</p>
                        </div>
                    </div>
                )}
                <AudioControls
                    isPlaying={isPlaying}
                    onPlayPauseClick={setIsPlaying}
                />
            </div>
        </footer>
    );
};

export default AudioPlayer;
