import React, {useState} from 'react';
import defaultImage from '../../Img/DefaultAvatar.png';
import {ref, uploadBytes, getDownloadURL, deleteObject} from "firebase/storage";
import { storage } from "../../firebase";
import { addProduct  } from "../../utils/Api/ProductApi";
import {Guid} from "guid-typescript";
const ProductPage = () => {
    const [selectedColor, setSelectedColor] = useState(null);
    const Design = ['LowTop', 'HighTop', 'Mule'];
    const ProductLine = ['Basas', 'Vintas', 'Urbas', 'Pattas', 'Socks', 'Shoelaces'];
    const Material = ['Canvas', 'Suede', 'Leather', 'Flannel', 'Corduroy'];
    const Color = [
        'Gray', 'White', 'Cream', 'Charcoal', 'Teal', 'Beige', 'Silver', 'NavyBlue',
        'OliveGreen', 'Lavender', 'Coral', 'CarrotOrange', 'CrimsonRed'
    ];
    const [image, setImage] = useState(null);
    const [imageUrl, setImageUrl] = useState("");
    const [imagePreview, setImagePreview] = useState(null);

    function getColorValue(colorName) {
        switch (colorName.toLowerCase()) {
            case 'gray':
                return '#808080';
            case 'white':
                return '#FFFFFF';
            case 'cream':
                return '#FFFDD0';
            case 'charcoal':
                return '#36454F';
            case 'teal':
                return '#008080';
            case 'beige':
                return '#F5F5DC';
            case 'silver':
                return '#C0C0C0';
            case 'navyblue':
                return '#000080';
            case 'olivegreen':
                return '#808000';
            case 'lavender':
                return '#E6E6FA';
            case 'coral':
                return '#FF7F50';
            case 'carrotorange':
                return '#ED9121';
            case 'crimsonred':
                return '#DC143C';
            default:
                return '#FFFFFF'; // Default color (white)
        }
    }
    const newProductId = Guid.newGuid(); // Replace with your method of generating a Guid

    const handleImageUpload = async (event) => {
        const file = event.target.files[0];
        if (file) {
            setImage(file);
            const previewUrl = URL.createObjectURL(file);
            setImagePreview(previewUrl); // Update the image preview URL
        }
    };
    const handleSaveProduct = async () => {
        if (image) {
            const imageRef = ref(storage, `products/${newProductId}/${image.name}`);
            try {
                const snapshot = await uploadBytes(imageRef, image);

                const url = await getDownloadURL(snapshot.ref);
                setImageUrl(url);
                setImage(image);
            } catch (error) {
                console.error("Error uploading the image: ", error);
            }
        }
        const newProduct = {
            ProductId: newProductId,
            ImageUrls: [imageUrl],
        };

        try {
            const response = await addProduct(newProduct);
            console.log('Product added successfully:', response);
        } catch (error) {
            console.error('Error adding product:', error);
        }
    };
    let selectedProduct;
    let productList;
    let productDetails;
    return (
        <div style={{ display: 'flex', justifyContent: 'center', maxWidth: '2000px', margin: '20px auto', gap: '20px',border: '1px solid #ccc'  }}>
            {/* Product List */}
            <div style={{ width: '25%', background: 'white', padding: '20px',border: '1px solid #ccc'  }}>
                <h1>Product List</h1>
                <table style={{ width: '100%', borderCollapse: 'collapse', border: '1px solid #ccc' }}>
                    <thead>
                    <tr style={{ backgroundColor: '#f2f2f2' }}>
                        <th style={{ padding: '10px', textAlign: 'left' }}>ID</th>
                        <th style={{ padding: '5px', textAlign: 'left' }}>Name</th>
                        <th style={{ padding: '5px', textAlign: 'left' }}>Stock</th>
                    </tr>
                    </thead>
                    <tbody>
                    {productList.map((product) => (
                        <tr key={product.id} style={{ backgroundColor: 'white',border: '1px solid #ccc' }}>
                            <td style={{ padding: '10px', textAlign: 'left' }}>{product.id}</td>
                            <td style={{ padding: '5px', textAlign: 'left' }}>{product.name}</td>
                            <td style={{ padding: '5px', textAlign: 'left' }}>{product.inStock ? 'In Stock' : 'Out of Stock'}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>

            {/* Product Details */}
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start',width: '75%', background: 'white', padding: '10px', boxSizing: 'border-box',border: '1px solid #ccc'  }}>
                {/* Left Section */}
                <div style={{ width: '80%', background: 'white', padding: '20px', boxSizing: 'border-box', border: '1px solid #ccc' }}>
                    {/* buttons */}
                    <button style={{ backgroundColor: 'blue', color: 'white', padding: '15px',fontSize: '20px',fontWeight: 'bold' }}>Add</button>
                    <button style={{ backgroundColor: 'red', color: 'white', padding: '15px',fontSize: '20px',fontWeight: 'bold' }}>Delete</button>
                    <button style={{ backgroundColor: 'green', color: 'white', padding: '15px',fontSize: '20px',fontWeight: 'bold' }}>Save</button>
                    {/* information */}
                    <div style={{ display: 'flex', marginBottom: '20px',border: '1px solid #ccc' }}>
                        <input type="file" accept="image/*" onChange={handleImageUpload} style={{ display: 'none' }} id="imageUpload" />
                        <label htmlFor="imageUpload">
                            <img src={imagePreview || defaultImage} alt="Product" style={{ width: '400px', height: '400px', boxSizing: 'border-box', border: '1px solid #ccc', cursor: 'pointer' }} />
                        </label>                        <div style={{ display: 'flex', flexDirection: 'column', marginLeft: '10px', width: '68%',marginBottom: '5px', boxSizing: 'border-box' }}>
                            <div style={{ marginBottom: '2px',height: '20%'}}>
                                <h3 style={{ marginBottom: '2px'}}>Name:</h3>
                                <input type="text" value={selectedProduct.name} onChange={(e) => console.log(e.target.value)} style={{ width: '100%',height: '50%', boxSizing: 'border-box' }} />
                            </div>
                            <div style={{marginBottom: '2px',height: '80%'}}>
                                <h3 style={{ marginBottom: '5px' }}>Description:</h3>
                                <textarea value={selectedProduct.description} onChange={(e) => console.log(e.target.value)} style={{ width: '100%',height: '80%', boxSizing: 'border-box' }} />
                            </div>
                        </div>
                    </div>
                    {/* product size */}
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', padding: '20px' }}>
                        {/* Table on the Left */}
                        <div style={{ flex: 1, marginRight: '20px' }}>
                            <table style={{ width: '100%', borderCollapse: 'collapse', border: '1px solid #ccc' }}>
                                <thead>
                                <tr style={{ backgroundColor: '#f2f2f2' }}>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Size</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Price</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Quantity</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Add</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Edit</th>
                                </tr>
                                </thead>
                                <tbody>
                                {productDetails.map((size, index) => (
                                    <tr key={index} style={{ backgroundColor: 'white',border: '1px solid #ccc' }}>
                                        <td style={{ padding: '10px', textAlign: 'left' }}>{size.size}</td>
                                        <td style={{ padding: '10px', textAlign: 'left' }}>{size.price}</td>
                                        <td style={{ padding: '10px', textAlign: 'left' }}>{size.quantity}</td>
                                        <td style={{ padding: '10px', textAlign: 'left' }}><button>Add</button></td>
                                        <td style={{ padding: '10px', textAlign: 'left' }}><button>Edit</button></td>
                                    </tr>
                                ))}
                                </tbody>
                            </table>
                        </div>
                        {/* Buttons on the Right */}

                    </div>
                </div>
                {/* Right Section */}
                <div style={{ width: '20%', background: 'white', padding: '20px', boxSizing: 'border-box', border: '1px solid #ccc' }}>
                    {/* Design List */}
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Design</h3>
                        <select style={{ width: '100%' }}>
                            {Object.values(Design).map((design, index) => (
                                <option key={index}>{design}</option>
                            ))}
                        </select>
                    </div>
                    {/* Product Line List */}
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Product Line</h3>
                        <select style={{ width: '100%' }}>
                            {Object.values(ProductLine).map((productLine, index) => (
                                <option key={index}>{productLine}</option>
                            ))}
                        </select>
                    </div>
                    {/* Material List */}
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Material</h3>
                        <select style={{ width: '100%' }}>
                            {Object.values(Material).map((material, index) => (
                                <option key={index}>{material}</option>
                            ))}
                        </select>
                    </div>
                    {/* Color List */}
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Color</h3>
                        <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                            {Object.values(Color).map((color, index) => (
                                <div
                                    key={index}
                                    onClick={() => setSelectedColor(color)} // Set the selected color
                                    title={color}
                                    style={{
                                        width: '30px',
                                        height: '30px',
                                        backgroundColor: getColorValue(color),
                                        margin: '5px',
                                        boxShadow: `0 0 0 2px ${selectedColor === color ? 'black' : (getColorValue(color) === '#FFFFFF' ? '#ccc' : 'transparent')}`,
                                        cursor: 'pointer',
                                        boxSizing: 'border-box',
                                        display: 'flex',
                                        justifyContent: 'center',
                                        alignItems: 'center',
                                    }}>
                                    {color === 'White' && (
                                        <span style={{ opacity: 0, fontSize: '10px' }}>
                                            {color}
                                        </span>
                                    )}
                                </div>
                            ))}
                        </div>
                    </div>

                </div>

            </div>
        </div>
    );
};

export default ProductPage;
