import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../Cart/CartContext';
import { addOrder } from '../../utils/Api/OrderApi';
import { PayPalScriptProvider, PayPalButtons } from '@paypal/react-paypal-js';

const CheckoutPage = () => {
    const navigate = useNavigate();
    const { cartItems } = useCart();
    const [customerInfo, setCustomerInfo] = useState({
        userId: '',
        name: '',
        email: '',
        phoneNumber: '',
        address: ''
    });

    // Memoize the total price calculation to avoid re-calculating on every render.
    const calculateTotalPrice = React.useMemo(() => {
        return cartItems.reduce((total, item) => total + item.product.price * item.quantity, 0);
    }, [cartItems]);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setCustomerInfo(prev => ({ ...prev, [name]: value }));
    };

    // Load user data from local storage and update state
    useEffect(() => {
        const userData = JSON.parse(localStorage.getItem('user') || '{}');
        if (userData) {
            setCustomerInfo({
                userId: userData.Id || '',
                name: userData.UserName || '',
                email: userData.Email || '',
                phoneNumber: userData.PhoneNumber || '',
                address: userData.Address || ''
            });
        }
        console.log('userdata: ',customerInfo)
    }, []);

    // Function to handle order placement
    const handlePlaceOrder = async (orderId) => {
        const totalPrice = calculateTotalPrice();
        const orderInfo = {
            orderId,
            userId: customerInfo.userId,
            name: customerInfo.name,
            email: customerInfo.email,
            phoneNumber: customerInfo.phoneNumber,
            address: customerInfo.address,
            cartItems: cartItems.map(item => ({
                productId: item.product.productId,
                quantity: item.quantity,
                size: item.size
            })),
            totalPrice
        };

        try {
            await addOrder(orderInfo);
            navigate('/paymentSuccess');
        } catch (error) {
            console.error('Error placing order:', error);
            navigate('/paymentFailure');
        }
    };


    return (
        <div>
            <h2>Checkout</h2>
            <form>
                {['name', 'email', 'phoneNumber', 'address'].map(field => (
                    <div key={field}>
                        <label>{field.charAt(0).toUpperCase() + field.slice(1)}:</label>
                        <input
                            type={field === 'email' ? 'email' : field === 'phoneNumber' ? 'tel' : 'text'}
                            value={customerInfo[field]}
                            onChange={handleInputChange}
                            name={field}
                        />
                    </div>
                ))}
            </form>
            <h2>Order Summary</h2>
            <div>
                {cartItems.map((item, index) => (
                    <div key={index}>
                        <p>Product: {item.product.name}</p>
                        <p>Size: {item.size}</p>
                        <p>Quantity: {item.quantity}</p>
                        <p>Total Price: {(item.product.price * item.quantity).toLocaleString()} USD</p>
                    </div>
                ))}
            </div>
            <p>Total Order Price: {calculateTotalPrice().toLocaleString()} USD</p>
            <PayPalScriptProvider options={{ 'client-id': 'AXA4WPpo8c9JinRBBe3a-OAPC0INyxT9eD5iAU5VI2DXX2dolTrIrKG7PdTT6k-2QDG2T2ZwzgIK02IC' }}>
                <PayPalButtons
                    createOrder={(data, actions) => actions.order.create({
                        purchase_units: [{
                            amount: { value: calculateTotalPrice().toFixed(2) }
                        }]
                    })}
                    onApprove={async (data, actions) => {
                        const order = await actions.order.capture();
                        console.log('paypal detail: ',order)
                        await handlePlaceOrder(order.id);
                    }}
                    onError={err => console.error('PayPal error: ', err)}
                />
            </PayPalScriptProvider>
        </div>
    );
};

export default CheckoutPage;
