// Search.js
import React, { useState } from 'react';
import { searchByTitle } from "../Api/SongApi"; // Adjust import path as necessary
import { Link } from 'react-router-dom';

const SearchPage = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);

    const handleSearch = async () => {
        if (!searchQuery) return; // Don't search if query is empty
        try {
            const results = await searchByTitle(searchQuery);
            setSearchResults(results);
        } catch (error) {
            console.error('Error searching:', error);
            setSearchResults([]); // Reset/clear results on error
        }
    };

    const handleInputChange = (event) => {
        setSearchQuery(event.target.value);
    };

    return (
        <div className="search-page-container">
            <h2>Search Songs</h2>
            <input
                type="text"
                value={searchQuery}
                onChange={handleInputChange}
                placeholder="Search by title..."
            />
            <button onClick={handleSearch}>Search</button>
            <div className="search-results">
                {searchResults.length > 0 ? (
                    <ul className="song-list">
                        {searchResults.map((song) => (
                            <li key={song.songId} className="song-item">
                                <div className="image-container-ListSong">
                                    {song.imageFileName && (
                                        <img src={song.imageFileName} alt={`${song.title} cover art`} /> // Update here
                                    )}
                                </div>
                                <div className="song-details">
                                    <strong>Title:</strong> {song.title}
                                </div>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No songs found. Try a different search.</p>
                )}
            </div>
        </div>
    );
};

export default SearchPage;
