// Search.js
import React, { useState } from 'react';
import { searchByTitle } from "../Api/SongApi"; // Adjust import path as necessary
import { Link } from 'react-router-dom';

const SearchPage = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState({ songs: [], users: [], albums: [], playlists: [] });

    const handleSearch = async () => {
        if (!searchQuery) return; // Don't search if query is empty
        try {
            const results = await searchByTitle(searchQuery);
            setSearchResults(results);
        } catch (error) {
            console.error('Error searching:', error);
            setSearchResults({ songs: [], users: [], albums: [], playlists: [] }); // Reset/clear results on error
        }
    };

    const handleInputChange = (event) => {
        setSearchQuery(event.target.value);
    };

    return (
        <div className="search-page-container">
            <h2>Search Songs</h2>
            <input
                type="text"
                value={searchQuery}
                onChange={handleInputChange}
                placeholder="Search by title..."
            />
            <button onClick={handleSearch}>Search</button>

            <div className="search-results">
                {/* Songs */}
                {searchResults.songs.length > 0 && (
                    <div>
                        <h3>Songs</h3>
                        <ul>
                            {searchResults.songs.map(song => (
                                <li key={song.songId}>
                                    {song.imageFileUrl && (
                                        <img src={song.imageFileUrl} alt={`${song.title} cover art`} style={{width:'100px', height:'100px',border:'100px'}} /> // Update here
                                    )}
                                    <span>{song.title} - Duration: {song.duration}</span>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}

                {/* Artists/Users */}
                {searchResults.users.length > 0 && (
                    <div>
                        <h3>Artists</h3>
                        <ul>
                            {searchResults.users.map(user => (
                                <li key={user.id}>
                                    <span>{user.userName}</span>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}

                {/* Albums */}
                {searchResults.albums.length > 0 && (
                    <div>
                        <h3>Albums</h3>
                        <ul>
                            {searchResults.albums.map(album => (
                                <li key={album.albumId}>
                                    <span>{album.nameOfAlbum}</span>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}

                {/* Playlists */}
                {searchResults.playlists.length > 0 && (
                    <div>
                        <h3>Playlists</h3>
                        <ul>
                            {searchResults.playlists.map(playlist => (
                                <li key={playlist.playlistId}>
                                    <span>{playlist.title}</span>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
};

export default SearchPage;
