import React, { useState } from 'react';
import {login, loginViaGg} from "../../utils/Api/AuthApi";
import { useNavigate } from 'react-router-dom';
import {jwtDecode} from "jwt-decode";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import './Login.css'
const clientId = "862905097670-678pkbir60a8v0jk4v75ua6nsu4j3k40.apps.googleusercontent.com";

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [loggedIn, setLoggedIn] = useState(false);
    const navigate = useNavigate(); // Get the navigate function

    const handleSubmit = async (e) => {
        e.preventDefault();

        const credentials = {
            email,
            password,
            rememberMe
        };
        try {
            console.log(email)
            console.log(password)
            const data = await login(credentials);
            if (data && data.accessToken) {
                console.log('User logged in successfully.');
                console.log('user data: ',data)
                setLoggedIn(true);
            } else {
                console.error("Login was successful but no token was received.");
                setErrorMessage("Login was successful but no token was received.");
            }
        } catch (error) {
            const errorMsg = error.response?.data?.message || "An error occurred during login.";
            setErrorMessage(errorMsg);
            console.error("Login error:", errorMsg);
        }
    };
    const handleLoginSuccess = async (response) => {
        console.log('Login successful:', response);
        const decoded = jwtDecode(response.credential);
        console.log('Decoded JWT:', decoded);
        const { email } = decoded;
        try {
            const responseLogin = await loginViaGg(email);
            console.log('response: ', responseLogin);
            if (responseLogin && responseLogin.accessToken) {
                console.log('user response: ',responseLogin)
                setLoggedIn(true);
            } else {
                setErrorMessage("Failed to log in via Google.");
            }
        } catch (error) {
            console.error('Error during login via Google:', error);
            setErrorMessage("An error occurred during login via Google.");
        }
    };

    const handleLoginFailure = (response) => {
        console.log('Login failed:', response);
    };
    if (loggedIn) {
        navigate('/');
        window.location.reload();
    }
    return (
        <div className="login-container">
            <h2>Login</h2>
            {errorMessage && <div className="error-message">{errorMessage}</div>}
            <form onSubmit={handleSubmit} className="login-form">
                <div className="form-group">
                    <label>Email:</label>
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </div>
                <div className="form-group">
                    <label>Password:</label>
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                </div>
                <div className="form-group">
                    <label>
                        <input type="checkbox" checked={rememberMe} onChange={(e) => setRememberMe(e.target.checked)} />
                        Remember me
                    </label>
                </div>
                <button type="submit" className="login-button">Login</button>
                <GoogleOAuthProvider clientId={clientId}>
                    <div className="google-login-container">
                        <h2>Login with Google</h2>
                        <GoogleLogin
                            onSuccess={handleLoginSuccess}
                            onError={handleLoginFailure}
                            scope="profile email"
                        />
                    </div>
                </GoogleOAuthProvider>
            </form>
        </div>
    );
};

export default Login;
