import React, { useState } from 'react';
import { register } from '../Api/api';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const Registration = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [gender, setGender] = useState('NonBinary'); // Default value for gender
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate(); // Initialize useNavigate

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (password !== confirmPassword) {
            setErrorMessage('The password and confirmation password do not match.');
            return;
        }



        // Use the map to get the numerical value of the selected gender

        const newUser = {
            email,
            password,
            confirmPassword,
        };

        console.log(newUser);

        try {
            const response = await register(newUser);
            // Check if registration was successful
            if (response && response.message) {
                localStorage.setItem('role', response.role || 'User'); // Assuming role is part of response
                console.log(response.message);
                navigate('/');
            }
        } catch (error) {
            if (error.response && error.response.status === 409) {
                // Email already registered
                setErrorMessage('Email is already registered.');
            } else {
                // Other types of errors
                setErrorMessage('An error occurred while registering. Please try again.');
            }
        }
    };


    return (
        <div className="center-container"> {/* Container for centering */}
            <div className="registration-container">
                <h2>Register</h2>
                {errorMessage && <div className="error-message">{errorMessage}</div>}
                <form onSubmit={handleSubmit} className="registration-form">
                    <div className="form-group">
                        <label>Email:</label>
                        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    </div>
                    <div className="form-group">
                        <label>Password:</label>
                        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    </div>
                    <div className="form-group">
                        <label>Confirm Password:</label>
                        <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} required />
                    </div>
                    <button type="submit" className="registration-button">Register</button>
                </form>
            </div>
        </div>
    );
};

export default Registration;
