import React, { useEffect, useState } from 'react';
import { ref, uploadBytes, getDownloadURL, deleteObject,listAll } from "firebase/storage";
import { storage } from "../../firebase";
import {
    addProduct,
    deleteProductById,
    GetAllProductNamesAndIds,
    getProductById,
    updateProductById
} from "../../utils/Api/ProductApi";
import {addProductSize, getProductSizesByProductId} from "../../utils/Api/ProductSizeApi";
import { Guid } from "guid-typescript";

const ProductPage = () => {

    const [mainImage, setMainImage] = useState(null); // State to hold the main image
    const [listImages, setListImages] = useState([]);
    const [imagePreview, setImagePreview] = useState(null);
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [design, setDesign] = useState(0);
    const [productLine, setProductLine] = useState(0);
    const [material, setMaterial] = useState(0);
    const [color, setColor] = useState(0);
    const [products, setProducts] = useState([]);
    const [sizes, setSizes] = useState([]);
    const [newSize, setNewSize] = useState('');
    const [newQuantity, setNewQuantity] = useState('');

    // Arrays for dropdown options
    const Design = ['LowTop', 'HighTop', 'Mule'];
    const ProductLine = ['Basas', 'Vintas', 'Urbas', 'Pattas', 'Socks', 'Shoelaces'];
    const Material = ['Canvas', 'Suede', 'Leather', 'Flannel', 'Corduroy'];
    const Color = ['Gray', 'White', 'Cream', 'Charcoal', 'Teal', 'Beige', 'Silver', 'NavyBlue', 'OliveGreen', 'Lavender', 'Coral', 'CarrotOrange', 'CrimsonRed'];
    const Size = ['35', '36', '36,5', '37', '38', '38,5', '39', '40', '40,5', '41', '42', '42,5', '43', '44', '44,5', '45', '46'];

    // Maps for converting dropdown values to numbers
    const designMap = { LowTop: 0, HighTop: 1, Mule: 2 };
    const productLineMap = { Basas: 0, Vintas: 1, Urbas: 2, Pattas: 3, Socks: 4, Shoelaces: 5 };
    const materialMap = { Canvas: 0, Suede: 1, Leather: 2, Flannel: 3, Corduroy: 4 };
    const colorMap = {Gray: 0, White: 1, Cream: 2, Charcoal: 3, Teal: 4, Beige: 5, Silver: 6, NavyBlue: 7, OliveGreen: 8, Lavender: 9, Coral: 10, CarrotOrange: 11, CrimsonRed: 12};

    const [selectedProduct, setSelectedProduct] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const productsData = await GetAllProductNamesAndIds();
                console.log('product data list: ',productsData)
                setProducts(productsData);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);
    const handleMainImageUpload = (file) => {
        if (file) {
            setMainImage(file);
        }
    };
    const handleMainImageClick = (index) => {
        if (listImages[index]) {
            setMainImage(listImages[index]);
        }
        else {
            setMainImage(listImages[0]);
        }
    };
    const handleDeleteAllImages = () => {
        const confirmDelete = window.confirm('Are you sure you want to delete all images?');
        if (confirmDelete) {
            setMainImage(null);
            setListImages([]);
        }
    };
    const handleProductClick = async (productId) => {
        try {
            const productData = await getProductById(productId);
            console.log('Product details:', productData);

            // Update state hooks with product details
            setName(productData.name);
            setDescription(productData.description);
            setPrice(productData.price.toString());
            setDesign(Design[productData.design]);
            setProductLine(ProductLine[productData.productLine]);
            setMaterial(Material[productData.material]);
            setColor(Color[productData.color]);

            setMainImage(productData.imageUrls[0]);
            setListImages(productData.imageUrls);
            setSelectedProduct(productData);

            const productSizes = await getProductSizesByProductId(productId);
            console.log('Product sizes:', productSizes);
            setSizes(productSizes);
        } catch (error) {
            console.error('Error fetching product details:', error);
        }
    };
    const handleAddNewSize = () => {
        if (newSize && newQuantity) {
            const newSizeObject = { size: newSize, quantity: parseInt(newQuantity), addQuantity: '' };
            const updatedSizes = [...sizes, newSizeObject];
            setSizes(updatedSizes);
            setNewSize('');
            setNewQuantity('');
        } else {
            console.error('Please provide both size and quantity');
        }
    };
    const handleSizeChange = (index, field, value) => {
        const updatedSizes = [...sizes];
        if (field === 'addQuantity') {
            updatedSizes[index][field] = isNaN(value) ? '' : parseInt(value);
        } else if (field === 'size') {
            const sanitizedValue = value.replace(',', '');
            updatedSizes[index][field] = sanitizedValue;
        } else {
            updatedSizes[index][field] = value;
        }
        setSizes(updatedSizes);
    };
    const handleImageUpload = (event) => {
        const files = event.target.files;
        const fileArray = Array.from(files);
        if (fileArray.length === 0) return;
        const listImagePreviews = fileArray.map(file => URL.createObjectURL(file));
        setListImages([...listImages, ...listImagePreviews]);
        if (!mainImage && listImagePreviews.length > 0) {
            setMainImage(listImagePreviews[0]);
        }
    };
    const handleDeleteImage = (index) => {
        const updatedListImages = [...listImages];
        updatedListImages.splice(index, 1);
        setListImages(updatedListImages);
    };
    //Function to save product data
    const handleSaveProduct = async () => {
        if (listImages.length === 0) {
            console.error('No images to upload');
            return;
        }

        const newProductId = Guid.create().toString();
        const imageUrls = [];
        try {
            await Promise.all(listImages.map(async (image, index) => {
                const imageRef = ref(storage, `products/${newProductId}/image_${index}`);
                console.log('Image reference:', imageRef.fullPath);
                try {
                    const blob = await fetch(image).then(res => res.blob());
                    const snapshot = await uploadBytes(imageRef, blob);
                    const imageUrl = await getDownloadURL(snapshot.ref);
                    imageUrls.push(imageUrl);
                } catch (error) {
                    console.error('Error uploading image:', error);
                }
            }));

            const numericDesign = designMap[design];
            const numericProductLine = productLineMap[productLine];
            const numericMaterial = materialMap[material];
            const numericColor = colorMap[color];

            const updatedSizes = sizes.map(size => {
                const sizeValue = size.size;
                const sizeIndex = Size.indexOf(sizeValue);
                if (sizeValue && sizeIndex !== -1) {
                    return {
                        size: sizeIndex,
                        quantity: (parseInt(size.quantity) || 0) + (parseInt(size.addQuantity) || 0),
                        productSizeId: `${sizeValue}-${newProductId}`
                    };
                } else {
                    console.error('Size value is undefined or not found in Size array:', sizeValue);
                    return null;
                }
            }).filter(size => size !== null);

            const productToSave = {
                name,
                description,
                design: numericDesign,
                productLine: numericProductLine,
                material: numericMaterial,
                color: numericColor,
                price: parseFloat(price),
                sizes: updatedSizes.map(size => ({
                    ...size,
                    productSizeId: `${Size[size.size]}-${newProductId}`,
                })),
                productId:newProductId,
                imageUrls: imageUrls,
            };

            console.log('product: ', productToSave);

            const response = await addProduct(productToSave);
            console.log('Product added successfully:', response);

            await Promise.all(updatedSizes.map(async (size) => {
                const productSizeData = {
                    ProductSizeId: size.productSizeId,
                    Size: size.size,
                    Quantity: size.quantity,
                    ProductId: newProductId
                };
                console.log('product size data',productSizeData)
                await addProductSize(productSizeData);
            }));

            console.log('Product sizes added successfully');
        } catch (error) {
            console.error('Error saving product:', error);
        }
    };
    const handleEditProduct = async (productId) => {
        try {
            if (!selectedProduct) {
                console.error('No product selected for editing');
                return;
            }
            const imagesFolderRef = ref(storage, `products/${productId}`);
            const items = await listAll(imagesFolderRef);
            await Promise.all(items.items.map(async (item) => {
                await deleteObject(item);
                console.log('Deleted file:', item.name);
            }));
            console.log('Old images deleted');
            const imageUrls = [];
            await Promise.all(listImages.map(async (image, index) => {
                const imageRef = ref(storage, `products/${productId}/image_${index}`);
                try {
                    const blob = await fetch(image).then(res => res.blob());
                    const snapshot = await uploadBytes(imageRef, blob);
                    const imageUrl = await getDownloadURL(snapshot.ref);
                    imageUrls.push(imageUrl);
                } catch (error) {
                    console.error('Error uploading image:', error);
                }
            }));
            const numericDesign = designMap[design];
            const numericProductLine = productLineMap[productLine];
            const numericMaterial = materialMap[material];
            const numericColor = colorMap[color];

            const updatedSizes = sizes.map(size => {
                const sizeValue = size.size;
                const sizeIndex = Size.indexOf(sizeValue);
                if (sizeValue && sizeIndex !== -1) {
                    return {
                        size: sizeIndex,
                        quantity: (parseInt(size.quantity) || 0) + (parseInt(size.addQuantity) || 0),
                        productSizeId: `${sizeValue}-${productId}`
                    };
                } else {
                    console.error('Size value is undefined or not found in Size array:', sizeValue);
                    return null;
                }
            }).filter(size => size !== null);

            const updatedProduct = {
                productId,
                name,
                description,
                design: numericDesign,
                productLine: numericProductLine,
                material: numericMaterial,
                color: numericColor,
                price: parseFloat(price),
                sizes: updatedSizes.map(size => ({
                    ...size,
                    productSizeId: `${Size[size.size]}-${productId}`,
                })),
                imageUrls: imageUrls,
            };

            console.log('Updated product:', updatedProduct);

            // Step 6: Save the updated product details
            const response = await updateProductById(productId, updatedProduct);
            console.log('Product updated successfully:', response);
        } catch (error) {
            console.error('Error updating product:', error);
        }
    };
    const handleDeleteProduct = async (productId) => {
        try {
            const response = await deleteProductById(productId);
            console.log('Product deleted successfully:', response);

        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };
    return (
        <div style={{ display: 'flex', justifyContent: 'center', maxWidth: '2000px', margin: '20px auto', gap: '20px', border: '1px solid #ccc' }}>
            {/* Product List */}
            <div style={{ width: '20%', background: 'white', padding: '20px', border: '1px solid #ccc' }}>
                <h1>Product List</h1>
                <table style={{ width: '100%', borderCollapse: 'collapse', border: '1px solid #ccc' }}>
                    <thead>
                    <tr style={{ backgroundColor: '#f2f2f2' }}>
                        <th style={{ padding: '5px', textAlign: 'left' }}>Name</th>
                    </tr>
                    </thead>
                    <tbody>
                    {products.map(product => (
                        <tr key={product.item1} onClick={() => handleProductClick(product.item1)}> {/* Pass product.id instead of product.productId */}
                            <td key={product.item1} style={{ padding: '5px', textAlign: 'left', cursor: 'pointer' }}>{product.item2}</td>
                        </tr>
                    ))}

                    </tbody>
                </table>
            </div>

            {/* Product Details */}
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', width: '80%', background: 'white', padding: '10px', boxSizing: 'border-box', border: '1px solid #ccc' }}>
                {/* Left Section */}
                <div style={{ width: '100%', background: 'white', padding: '10px', boxSizing: 'border-box', border: '1px solid #ccc' }}>
                    {/*Top information*/}
                    <div style={{ display: 'flex', marginBottom: '20px' }}>
                        {/* Main image */}
                        <div style={{ justifyContent: 'space-between',border: '1px solid #ccc',width: '70%',maxWidth:'350px',maxHeight:'350px' }}>
                            {/*<input type="file" accept="image/*" onChange={handleMainImageUpload} style={{ display: 'none' }} id="mainImageUploadInput" />*/}
                            <div style={{ position: 'relative', marginBottom: '2px',padding:'3px' }}>
                                {mainImage && (
                                    <img
                                        src={mainImage}
                                        alt="image"
                                        style={{ display:'flex',justifyContent: 'space-between' }}
                                    />
                                )}

                            </div>
                        </div>
                        {/* List images */}
                        <div style={{ display: 'flex', flexDirection: 'column', overflowX: 'scroll', marginBottom: '5px', width: '30%', maxHeight: '350px', maxWidth: '115px' }}>
                            <input type="file" accept="image/*" multiple onChange={handleImageUpload} style={{ marginBottom: '3px' }} />
                            {listImages.map((image, index) => (
                                <div key={index} style={{ position: 'relative' }}>
                                    <img src={image} alt={`Image ${index}`} style={{ maxWidth: '100px', maxHeight: '100px', objectFit: 'cover', cursor: 'pointer' }} onClick={() => handleMainImageClick(index)} />
                                    <button onClick={() => handleDeleteImage(index)} style={{ position: 'absolute', top: '1px', right: '1px', backgroundColor: 'gray', color: 'white', border: 'none', borderRadius: '50%', width: '18px', height: '18px', padding: '0', display: 'flex', justifyContent: 'center', alignItems: 'center', cursor: 'pointer' }}>X</button>
                                </div>
                            ))}
                        </div>
                        {/* Product Information */}
                        <div style={{ display: 'flex', flexDirection: 'column', width: '70%', height: '350px', border: '1px solid #ccc', marginRight: '10px', boxSizing: 'border-box' }}>
                            <div style={{ display: 'flex', flexDirection: 'column', height: '100%', boxSizing: 'border-box', padding: '4px' }}>
                                <div>
                                    <h3>Name:</h3>
                                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} style={{ width: '90%',height:'20px' }} />
                                </div>
                                <div>
                                    <h3>Description:</h3>
                                    <textarea value={description} onChange={(e) => setDescription(e.target.value)} style={{ width: '95%', height: '100px' }} />
                                </div>
                                <div>
                                    <h3>Price:</h3>
                                    <input type="text" value={price} onChange={(e) => setPrice(e.target.value)} style={{ width: '20%',height:'20px' }} />
                                </div>
                            </div>
                        </div>
                    </div>
                    {/*Buttons*/}
                    <div>
                        {listImages.length > 0 && (
                            <button onClick={handleDeleteAllImages}>Delete All Images</button>
                        )}
                        <button onClick={handleSaveProduct} style={{ backgroundColor: 'blue', color: 'white', padding: '15px', fontSize: '20px', fontWeight: 'bold' }}>Add</button>
                        {selectedProduct && (
                            <div>
                            <button onClick={() => handleEditProduct(selectedProduct.productId)} style={{ backgroundColor: 'green', color: 'white', padding: '15px', fontSize: '20px', fontWeight: 'bold' }}>Edit</button>
                            <button onClick={() => handleDeleteProduct(selectedProduct.productId)} style={{ backgroundColor: 'red', color: 'white', padding: '15px', fontSize: '20px', fontWeight: 'bold' }}>Delete</button>
                            </div>
                        )}
                    </div>
                    {/*design,line,material,color*/}
                    <div style={{ display: 'flex', flexDirection: 'row' }}>
                        <div style={{ flex: 1, marginRight: '20px' }}>
                            <h3>Design</h3>
                            <select style={{ width: '100%' }} value={design} onChange={(e) => setDesign(e.target.value)}>
                                {Design.map((designOption, index) => (
                                    <option key={designOption} value={designOption}>{designOption}</option>
                                ))}
                            </select>
                        </div>
                        <div style={{ flex: 1, marginRight: '20px' }}>
                            <h3>Product Line</h3>
                            <select style={{ width: '100%' }} value={productLine} onChange={(e) => setProductLine(e.target.value)}>
                                {ProductLine.map((productLine, index) => (
                                    <option key={index} value={productLine}>{productLine}</option>
                                ))}
                            </select>
                        </div>
                        <div style={{ flex: 1, marginRight: '20px' }}>
                            <h3>Material</h3>
                            <select style={{ width: '100%' }} value={material} onChange={(e) => setMaterial(e.target.value)}>
                                {Material.map((material, index) => (
                                    <option key={index} value={material}>{material}</option>
                                ))}
                            </select>
                        </div>
                        <div style={{ flex: 1 }}>
                            <h3>Color</h3>
                            <select style={{ width: '100%' }} value={color} onChange={(e) => setColor(e.target.value)}>
                                {Color.map((color, index) => (
                                    <option key={index} value={color}>{color}</option>
                                ))}
                            </select>
                        </div>
                    </div>
                    {/* Product size */}
                    <div style={{ display: 'flex', width: '800px', justifyContent: 'space-between', alignItems: 'flex-start', padding: '5px' }}>
                        <div style={{ flex: 1, marginRight: '10px', overflowY: 'auto', maxHeight: '400px' }}>
                            <table style={{ width: '100%', borderCollapse: 'collapse', border: '1px solid #ccc' }}>
                                <thead>
                                <tr style={{ backgroundColor: '#f2f2f2' }}>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Size</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Quantity</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Add Quantity</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Actions</th>
                                </tr>
                                </thead>
                                <tbody>
                                {sizes.length === 0 ? (
                                    <tr>
                                        <td colSpan="4" style={{ textAlign: 'center', padding: '10px' }}>No sizes added yet</td>
                                    </tr>
                                ) : (
                                    sizes.map((size, index) => (
                                        <tr key={index} style={{ backgroundColor: 'white', border: '1px solid #ccc' }}>
                                            <td style={{ padding: '10px', textAlign: 'left' }}>{size.size}</td>
                                            <td style={{ padding: '10px', textAlign: 'left' }}>{size.quantity}</td>
                                            <td style={{ padding: '10px', textAlign: 'left' }}>
                                                <input type="number" value={size.addQuantity} onChange={(e) => handleSizeChange(index, 'addQuantity', e.target.value)} />
                                            </td>
                                            <td style={{ padding: '5px', textAlign: 'left' }}>
                                                <button onClick={() => handleAddNewSize(index)}>Add</button>
                                            </td>
                                        </tr>
                                    ))
                                )}
                                <tr style={{ backgroundColor: 'white', border: '1px solid #ccc' }}>
                                    <td>
                                        <select value={newSize} onChange={(e) => setNewSize(e.target.value)}>
                                            <option value="">Size</option>
                                            {Size.map((size, index) => (
                                                <option key={index} value={size}>{size}</option>
                                            ))}
                                        </select>
                                    </td>
                                    <td>
                                        <input type="text" value={newQuantity} onChange={(e) => setNewQuantity(e.target.value)} />
                                    </td>
                                    <td>
                                        <button onClick={handleAddNewSize}>Add</button>
                                    </td>
                                </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>

                </div>
                {/* Right Section */}
                {/*<div style={{ width: '0%', background: 'white', padding: '20px', boxSizing: 'border-box', border: '1px solid #ccc' }}>*/}

                {/*    /!*<button style={{ backgroundColor: 'red', color: 'white', padding: '15px', fontSize: '20px', fontWeight: 'bold' }}>Delete</button>*!/*/}
                {/*    /!*<button onClick={handleSaveProduct} style={{ backgroundColor: 'green', color: 'white', padding: '15px', fontSize: '20px', fontWeight: 'bold' }}>Save</button>               *!/*/}
                {/*</div>*/}
            </div>
        </div>
    );
};

export default ProductPage;
