import React, { useEffect, useRef, useState } from 'react';
import AudioControls from './AudioControls';
import muteIcon from '../Icon/icons8-mute-50.png';
import lowVolumeIcon from '../Icon/icons8-low-volume-50.png';
import middleVolumeIcon from '../Icon/icons8-volume-50.png';
import volumeUpIcon from '../Icon/icons8-volume-up-50.png';

const AudioPlayer = ({ track, onPlayPauseClick }) => {
    const audioRef = useRef(new Audio(track.audioSrc));
    const [volume, setVolume] = useState(1);
    const [isMuted, setIsMuted] = useState(false);
    const [isPlaying, setIsPlaying] = useState(false); // Default state set to paused

    useEffect(() => {
        if (track && track.audioSrc) {
            audioRef.current.src = track.audioSrc;
            if (isPlaying) {
                audioRef.current.play();
            }
        }
    }, [track, isPlaying]);

    useEffect(() => {
        audioRef.current.volume = isMuted ? 0 : volume;
    }, [volume, isMuted]);

    const toggleMute = () => {
        setIsMuted(!isMuted);
    };

    const getVolumeIcon = () => {
        if (isMuted || volume === 0) return muteIcon;
        if (volume < 0.5) return lowVolumeIcon;
        if (volume < 1) return middleVolumeIcon;
        return volumeUpIcon;
    };

    const handlePlayPauseClick = () => {
        setIsPlaying(!isPlaying);
    };

    // if (!track) return null;
    const playerContainerStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        position: 'fixed',
        left: '0',
        bottom: '0',
        width: '100%',
        backgroundColor: '#333333',
        boxShadow: '0 -2px 5px rgba(0, 0, 0, 0.2)',
        padding: '1rem'
    };

    const trackInfoStyle = {
        display: 'flex',
        alignItems: 'center',
        padding: '0 1rem'
    };

    const trackImageStyle = {
        width: '50px',
        height: '50px',
        marginRight: '1rem',
        borderRadius: '50%'
    };

    const trackDetailsStyle = {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center'
    };

    const volumeControlStyle = {
        display: 'flex',
        alignItems: 'center',
        marginRight: '20px'
    };

    const volumeIconStyle = {
        width: '30px',
        height: '30px',
        cursor: 'pointer'
    };

    const volumeSliderStyle = {
        width: '100px'
    };

    return (
        <footer style={playerContainerStyle}>
            <div style={trackInfoStyle}>
                <img src={track.imageSrc} alt={track.title} style={trackImageStyle} />
                <div style={trackDetailsStyle}>
                    <h3>{track.title}</h3>
                    <p>{track.artist}</p>
                </div>
            </div>
            <AudioControls
                isPlaying={isPlaying}
                onPlayPauseClick={handlePlayPauseClick}
            />
            <div style={volumeControlStyle}>
                <img
                    src={getVolumeIcon()}
                    alt="Volume"
                    onClick={toggleMute}
                    style={volumeIconStyle}
                />
                <input
                    type="range"
                    value={isMuted ? 0 : volume}
                    min="0"
                    max="1"
                    step="0.01"
                    onChange={(e) => setVolume(parseFloat(e.target.value))}
                    style={volumeSliderStyle}
                />
                <button onClick={toggleMute}>{isMuted ? 'Unmute' : 'Mute'}</button>
            </div>
            <audio ref={audioRef} />
        </footer>
    );
};

export default AudioPlayer;
