import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllSongsByUserId, deleteSongById, getSongById } from '../../Api/SongApi';
import AudioPlayer from '../../Audio/AudioPlayer'; // Adjust the import path as needed
import { ref, deleteObject } from "firebase/storage";
import {storage} from "../../firebase"; // Import needed Firebase functions

const ListSong = ({ onTrackChange, onPlayPauseChange }) => {
    const [songs, setSongs] = useState([]);
    const [track,setTrack]=useState([])
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const [showDropdown, setShowDropdown] = useState(false);
    const navigate = useNavigate();
    const [isPlaying, setIsPlaying] = useState(false);
    const [isLoading, setIsLoading] = useState(false);

    const playTrack = (song) => {
        console.log('play song id: ', song.songId);
        onTrackChange({
            audioSrc: song.audioFileUrl,
            imageSrc: song.imageFileUrl,
            title: song.title,
            artist: song.artist, // Assuming you have an artist property
        });
        setIsPlaying(true);
    };



    useEffect(() => {
        if (!user || !user.Id) {
            console.error('User ID is missing');
            return;
        }
        getAllSongsByUserId(user.Id)
            .then(fetchedSongs => {
                setSongs(fetchedSongs.flat());
            })
            .catch(error => console.error('Error fetching songs:', error));
    }, [user.Id]);


    const handleDelete = async (songId) => {
        try {
            // Find the song in your state
            const song = songs.find(s => s.songId === songId);
            if (!song) {
                console.error("Song not found");
                return;
            }

            const audioFilePath = song.audioFileUrl.split('o/')[1].split('?')[0];
            const imageFilePath = song.imageFileUrl.split('o/')[1].split('?')[0];

            const audioFileRef = ref(storage, decodeURIComponent(audioFilePath));
            const imageFileRef = ref(storage, decodeURIComponent(imageFilePath));

            await deleteObject(audioFileRef).then(() => {
                console.log("Audio file deleted successfully");
            }).catch((error) => {
                console.error("Error deleting audio file:", error);
            });

            await deleteObject(imageFileRef).then(() => {
                console.log("Image file deleted successfully");
            }).catch((error) => {
                console.error("Error deleting image file:", error);
            });

            await deleteSongById(songId);
            setSongs(songs.filter(song => song.songId !== songId));

            console.log('Song and associated files deleted successfully');
        } catch (error) {
            console.error('Error deleting the song and files:', error);
        }
    };


    const handleEdit = (songId) => {
        console.log(`Editing song with ID: ${songId}`);
        navigate(`/EditSong/${songId}`);
    };

    const formatDuration = (duration) => {
        const [hours, minutes, seconds] = duration.split(':');
        return `${parseInt(hours) * 60 + parseInt(minutes)}:${seconds}`;
    };




    const handleMouseEnter = (event) => {
        event.target.parentNode.classList.add('placeholder');
    };
    const handleMouseLeave = (event) => {
        event.target.parentNode.classList.remove('placeholder');
    };
    const handleSongSelect = (songData) => {
        setTrack(songData);
    };

    if (isLoading) {
        return <div>Loading...</div>;
    }
    return (
        <div>
            <h2>Songs by User</h2>
            <ul>
                {songs.map((song) => (
                    <li key={song.songId} className="song-item" >
                        <div className="song-info" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                            <div className="image-container-ListSong">
                                {song.imageFileUrl && (
                                    <img src={song.imageFileUrl} alt={`${song.title} cover art`} /> // Update here
                                )}
                            </div>
                            <div className="song-details">
                                <strong>Title:</strong> {song.title}
                            </div>
                            <div className="song-details">
                                <strong>Duration:</strong> {formatDuration(song.duration)}
                            </div>
                            <div className="song-details">
                                <strong>Album:</strong> {song.albumName}
                            </div>
                            <button onClick={() => playTrack(song)}>Play</button>
                            <button onClick={() => handleEdit(song.songId)} className="edit-button">
                                Edit
                            </button>
                            <button onClick={() => handleDelete(song.songId)} className="delete-button">
                                Delete
                            </button>

                        </div>
                    </li>
                ))}
            </ul>
            <style>
                {`
                .navbar-container {
                  display: flex;
                  justify-content: space-between;
                  align-items: center;
                  padding: 1rem;
                  background-color: #333;
                  color: #fff;
                }
                .navbar-menu {
                  list-style: none;
                  display: flex;
                  align-items: center;
                }
                .navbar-menu li {
                  margin-left: 1rem;
                }
                .navbar-menu li:first-child {
                  margin-left: 0;
                }
                .navbar-menu a {
                  color: #fff;
                  text-decoration: none;
                }
                button {
                  padding: 10px 20px;
                  margin: 5px;
                  background-color: #007bff;
                  color: #fff;
                  border: none;
                  border-radius: 5px;
                  cursor: pointer;
                }
                button:hover {
                  background-color: #0056b3;
                }
                .song-item {
                  border: 1px solid #ccc; 
                }
                .placeholder {
                  background-color: #f0f0f0;
                }
                `}
            </style>
        </div>
    );
};

export default ListSong;
