import React, { useState, useEffect, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { updateSongById, getSongById } from '../../Api/SongApi';
import { ref, uploadBytes, getDownloadURL, deleteObject } from "firebase/storage";
import { storage } from "../../firebase";

const EditSong = () => {
    const { songId } = useParams();
    const navigate = useNavigate();
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const [song, setSong] = useState({
        title: '',
        imageFileUrl: '',
        audioFileUrl: '',
        genre: '',
        privacy: 'public',
        imageFilePreview: '',
        audioFilePreview: '',
    });

    const genreToIntMap = useMemo(() => ({
        Workout: 0,
        Relaxation: 1,
        Party: 2,
        StudyFocus: 3,
        MoodBooster: 4,
        Chill: 5,
        Travel: 6,
        Morning: 7,
        Nighttime: 8,
        Throwback: 9,
        LoveSongs: 10,
        SadSongs: 11,
        Jazz: 12,
        Classical: 13,
        Indie: 14,
        Rock: 15,
        Pop: 16,
        HipHopRap: 17,
        Electronic: 18,
        RandBSoul: 19,
    }), []);

    const intToGenreMap = useMemo(() =>
            Object.keys(genreToIntMap).reduce((obj, key) => {
                obj[genreToIntMap[key]] = key;
                return obj;
            }, {}),
        [genreToIntMap]);

    useEffect(() => {
        const fetchSong = async () => {
            try {
                const songData = await getSongById(songId);
                setSong(prevState => ({
                    ...prevState,
                    title: songData.title,
                    genre: intToGenreMap[songData.nameOfGenre],
                    privacy: songData.privacy ? 'public' : 'private',
                    imageFileUrl: songData.imageFileUrl,
                    audioFileUrl: songData.audioFileUrl,
                    imageFilePreview: songData.imageFileUrl,
                    audioFilePreview: songData.audioFileUrl,
                }));
            } catch (error) {
                console.error('Error fetching song:', error);
            }
        };

        fetchSong();
    }, [songId, intToGenreMap]);

    const handleFileChange = (event) => {
        const { name, files } = event.target;
        if (files.length > 0) {
            const fileUrl = URL.createObjectURL(files[0]);
            setSong({ ...song, [`${name}Preview`]: fileUrl, [name]: files[0] });
        } else {
            setSong(prevState => ({ ...prevState, [`${name}Preview`]: prevState[name] }));
        }
    };



    const handleChange = (event) => {
        const { name, value } = event.target;
        setSong({ ...song, [name]: value });
    };

    // const handleSubmit = async (event) => {
    //     event.preventDefault();
    //
    //     let newImageFileUrl = song.imageFileUrl;
    //     let newAudioFileUrl = song.audioFileUrl;
    //
    //     if (song.imageFileUrl && typeof song.imageFileUrl !== 'string') {
    //         await deleteFile(song.imageFileUrl);
    //         newImageFileUrl = await uploadFile(song.imageFileUrl, `SoundTrackImage/${songId}`);
    //     }
    //
    //     if (song.audioFileUrl && typeof song.audioFileUrl !== 'string') {
    //         await deleteFile(song.audioFileUrl);
    //         newAudioFileUrl = await uploadFile(song.audioFileUrl, `SoundTrack/${songId}`);
    //     }
    //
    //     const updatedSongData = {
    //         SongId: songId,
    //         UserId: user.Id,
    //         Title: song.title,
    //         AudioFileUrl: newAudioFileUrl,
    //         ImageFileUrl: newImageFileUrl,
    //         GenreId: genreToIntMap[song.genre],
    //         Privacy: song.privacy === 'public',
    //     };
    //     // console.log('update data: ',updatedSongData)
    //
    //     try {
    //         await updateSongById(songId, updatedSongData);
    //         alert('Song updated successfully');
    //         //navigate('/ListSong');
    //     } catch (error) {
    //         console.error('Error updating the song:', error);
    //     }
    // };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('UserId', user.Id);
        formData.append('Title', song.title);
        formData.append('GenreId', genreToIntMap[song.genre]);
        formData.append('Privacy', song.privacy === 'public');

        if (song.imageFileUrl && typeof song.imageFileUrl !== 'string') {
            formData.append('ImageFile', song.imageFileUrl);
        }

        if (song.audioFilePreview && typeof song.audioFilePreview !== 'string') {
            formData.append('AudioFile', song.audioFilePreview);
        }

        try {
            await updateSongById(songId, formData);
            alert('Song updated successfully');
            navigate('/ListSong');
        } catch (error) {
            console.error('Error updating the song:', error);
        }
    };


    return (
        <div style={{ maxWidth: '600px', margin: 'auto', padding: '20px', background: '#f9f9f9', borderRadius: '10px', boxShadow: '0 0 10px rgba(0,0,0,0.1)' }}>
            <h2 style={{ textAlign: 'center' }}>Edit Song</h2>
            <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="title">Title:</label>
                    <input
                        id="title"
                        type="text"
                        name="title"
                        value={song.title}
                        onChange={handleChange}
                        placeholder="Enter song title"
                        style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}
                    />
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="genre">Genre:</label>
                    <select
                        id="genre"
                        name="genre"
                        value={song.genre}
                        onChange={handleChange}
                        style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}
                    >
                        {Object.entries(intToGenreMap).map(([key, value]) => (
                            <option key={key} value={value}>{value}</option>
                        ))}
                    </select>
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="privacy">Privacy:</label>
                    <select
                        id="privacy"
                        name="privacy"
                        value={song.privacy}
                        onChange={handleChange}
                        style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}
                    >
                        <option value="public">Public</option>
                        <option value="private">Private</option>
                    </select>
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="imageFile">Image:</label>
                    <input
                        id="imageFile"
                        type="file"
                        name="imageFile"
                        onChange={handleFileChange}
                        style={{ display: 'block', marginBottom: '10px' }}
                    />
                    {song.imageFilePreview && (
                        <div style={{ marginBottom: '15px' }}>
                            <label>Current Image:</label>
                            <img
                                src={song.imageFilePreview}
                                alt="Current Cover"
                                style={{ width: '150px', height: '150px', objectFit: 'cover', borderRadius: '4px' }}
                            />
                        </div>
                    )}
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="audioFile">Audio:</label>
                    <input
                        id="audioFile"
                        type="file"
                        name="audioFile"
                        onChange={handleFileChange}
                        style={{ display: 'block', marginBottom: '10px' }}
                    />
                    {song.audioFilePreview && (
                        <div style={{ marginBottom: '15px' }}>
                            <label>Current Audio:</label>
                            <audio controls src={song.audioFilePreview}>
                                Your browser does not support the audio element.
                            </audio>
                        </div>
                    )}
                </div>
                <div>
                    <button type="submit" style={{ marginRight: '10px', padding: '10px 20px', border: 'none', background: '#007bff', color: '#fff', borderRadius: '5px', cursor: 'pointer' }}>Save Changes</button>
                    <button type="button" onClick={() => navigate('/ListSong')} style={{ padding: '10px 20px', border: 'none', background: '#dc3545', color: '#fff', borderRadius: '5px', cursor: 'pointer' }}>Cancel</button>
                </div>
            </form>
        </div>
    );
};

export default EditSong;
