import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllSongsByUserId, deleteSongById,streamSongById } from '../../Api/SongApi';

const ListSong = () => {
    const [songs, setSongs] = useState([]);
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const [showDropdown, setShowDropdown] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (!user || !user.Id) {
            console.error('User ID is missing');
            return;
        }
        getAllSongsByUserId(user.Id)
            .then(fetchedSongs => {
                setSongs(fetchedSongs.flat());
            })
            .catch(error => console.error('Error fetching songs:', error));
    }, [user.Id]);


    const handleDelete = async (songId) => {
        try {
            await deleteSongById(songId);
            setSongs(songs.filter(song => song.songId !== songId));
        } catch (error) {
            console.error('Error deleting the song:', error);
        }
    };
    const handleEdit = (songId) => {
        console.log(`Editing song with ID: ${songId}`);
        navigate(`/EditSong/${songId}`);
    };
    const formatDuration = (duration) => {
        const [hours, minutes, seconds] = duration.split(':');
        return `${parseInt(hours) * 60 + parseInt(minutes)}:${seconds}`;
    };

    const handleStreamSong = async (songId) => {
        try {
            const blob = await streamSongById(songId);
            const url = URL.createObjectURL(blob);
            console.log('Stream URL:', url);
            // For now, just log the URL. You can use this URL in an <audio> tag to play the audio later.
        } catch (error) {
            console.error('Error streaming the song:', error);
        }
    };


    return (
        <div>
            <h2>Songs by User</h2>
            <ul>
                {songs.map((song) => (
                    <li key={song.songId} className="song-item" onClick={() => handleStreamSong(song.songId)}>
                        <div className="song-info">
                            <div className="image-container-ListSong">
                                {song.imageData && (
                                    <img src={`data:image/png;base64,${song.imageData}`} alt={`${song.title} cover art`} />
                                )}
                            </div>
                            <div className="song-details">
                                <strong>Title:</strong> {song.title}
                            </div>
                            <div className="song-details">
                                <strong>Duration:</strong> {formatDuration(song.duration)}
                            </div>
                            <div className="song-details">
                                <strong>Album:</strong> {song.albumName}
                            </div>
                            <button onClick={() => handleEdit(song.songId)} className="edit-button">
                                Edit
                            </button>
                            <button onClick={() => handleDelete(song.songId)} className="delete-button">
                                Delete
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
            <style>
                {`
                .navbar-container {
                  display: flex;
                  justify-content: space-between;
                  align-items: center;
                  padding: 1rem;
                  background-color: #333;
                  color: #fff;
                }
                .navbar-menu {
                  list-style: none;
                  display: flex;
                  align-items: center;
                }
                .navbar-menu li {
                  margin-left: 1rem;
                }
                .navbar-menu li:first-child {
                  margin-left: 0;
                }
                .navbar-menu a {
                  color: #fff;
                  text-decoration: none;
                }
                button {
                  padding: 10px 20px;
                  margin: 5px;
                  background-color: #007bff;
                  color: #fff;
                  border: none;
                  border-radius: 5px;
                  cursor: pointer;
                }
                button:hover {
                  background-color: #0056b3;
                }
                `}
            </style>
        </div>
    );
};

export default ListSong;
