import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
    // State to manage whether user is authenticated
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [user, setUser] = useState(null);
    const [showDropdown, setShowDropdown] = useState(false);

    // Function to handle logout
    const onLogout = () => {
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        setIsAuthenticated(false); // Update authentication state
        setUser(null); // Clear user state
        window.location.reload(); // Reload the web page
    };

    useEffect(() => {
        // Check if user exists in local storage on component mount
        const user = localStorage.getItem('user');
        if (user) {
            setIsAuthenticated(true);
            setUser(JSON.parse(user));
        }
    }, []);

    const toggleDropdown = () => {
        setShowDropdown(!showDropdown);
    };
    const Modal = ({ onClose }) => {
        return (
            <div className="modal">
                <div className="modal-content">
                    <ul>
                        <li><a href="/account">Account</a></li>
                        <li><a href="/settings">Settings</a></li>
                        <li><button onClick={onClose}>Logout</button></li>
                    </ul>
                </div>
            </div>
        );
    };

    return (
        <nav>
            <div className="navbar-container">
                <div className="navbar-brand">
                    <Link to="/">WebName</Link>
                </div>
                <ul className="navbar-menu">
                    {!isAuthenticated && (
                        <>
                            <li>
                                <Link to="/register">Register</Link>
                            </li>
                            <li>
                                <Link to="/login">Login</Link>
                            </li>
                        </>
                    )}
                    {isAuthenticated && user && (
                        <>
                            <li onClick={toggleDropdown}>
                                <span className="username">{user.UserName}</span>
                                {showDropdown && (
                                    <ul className="dropdown-menu">
                                        <li><Link to="/account">Account</Link></li>
                                        <li><Link to="/settings">Settings</Link></li>
                                        <li><button onClick={onLogout}>Logout</button></li>
                                    </ul>
                                )}
                            </li>
                        </>
                    )}
                </ul>
            </div>
        </nav>
    );
};

export default Navbar;
