import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../Cart/CartContext';
import { addOrder } from '../../utils/Api/OrderApi';
import { PayPalScriptProvider, PayPalButtons } from '@paypal/react-paypal-js';

const CheckoutPage = () => {
    const history = useNavigate();
    const { cartItems } = useCart();
    const [customerInfo, setCustomerInfo] = useState({
        name: '',
        email: '',
        phoneNumber: '',
        address: ''
    });

    const calculateTotalPrice = () => cartItems.reduce((total, item) => (total + item.product.price * item.quantity), 0);

    useEffect(() => {
        // Load PayPal script dynamically
        const script = document.createElement('script');
        script.src = 'https://www.paypal.com/sdk/js?client-id=YOUR_CLIENT_ID'; // Replace with your PayPal client ID
        script.async = true;
        script.onload = () => {
            // Initialize PayPal script once loaded
            window.paypal.Buttons({
                createOrder: function(data, actions) {
                    return actions.order.create({
                        purchase_units: [{
                            amount: {
                                value: calculateTotalPrice().toFixed(2)
                            }
                        }]
                    });
                },
                onApprove: async function(data, actions) {
                    const order = await actions.order.capture();
                    // Handle order capture success
                    console.log('Order captured: ', order);
                    handlePlaceOrder();
                },
                onError: function(err) {
                    // Handle error
                    console.error('PayPal error: ', err);
                }
            }).render('#paypal-button-container');
        };
        document.body.appendChild(script);

        return () => {
            // Clean up the script on unmount
            document.body.removeChild(script);
        };
    }, [calculateTotalPrice]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCustomerInfo(prev => ({ ...prev, [name]: value }));
    };

    const handlePlaceOrder = async () => {
        const totalPrice = calculateTotalPrice();
        const orderInfo = {
            userId: getUserIdFromLocalStorage(),
            cartItems: cartItems.map(item => ({
                productId: item.product.productId,
                quantity: item.quantity,
                size: item.size
            })),
            ...customerInfo,
            totalPrice
        };

        try {
            await addOrder(orderInfo);
            history('/paymentSuccess');
        } catch (error) {
            console.error('Error placing order:', error);
            history('/paymentFailure');
        }
    };

    const getUserIdFromLocalStorage = () => {
        const userData = localStorage.getItem('user');
        return userData ? JSON.parse(userData).Id : null;
    };

    return (
        <div>
            <h2>Checkout</h2>
            <form>
                {['name', 'email', 'phoneNumber', 'address'].map(field => (
                    <div key={field}>
                        <label>{field.charAt(0).toUpperCase() + field.slice(1)}:</label>
                        <input
                            type={field === 'email' ? 'email' : field === 'phoneNumber' ? 'tel' : 'text'}
                            value={customerInfo[field]}
                            onChange={handleInputChange}
                            name={field}
                        />
                    </div>
                ))}
            </form>
            <h2>Order Summary</h2>
            <div>
                {cartItems.map((item, index) => (
                    <div key={index}>
                        <p>Product: {item.product.name}</p>
                        <p>Size: {item.size}</p>
                        <p>Quantity: {item.quantity}</p>
                        <p>Total Price: {(item.product.price * item.quantity).toLocaleString()} USD</p>
                    </div>
                ))}
            </div>
            <p>Total Order Price: {calculateTotalPrice().toLocaleString()} USD</p>
            <div id="paypal-button-container"></div>
        </div>
    );
};

export default CheckoutPage;
