import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [userRole, setUserRole] = useState(null);
    const [user, setUser] = useState(null);
    const [showDropdown, setShowDropdown] = useState(false);
    const dropdownRef = useRef(null); // Add this line
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const onLogout = () => {
        localStorage.removeItem('user');
        localStorage.removeItem('accessToken');
        localStorage.removeItem('refreshToken');
        localStorage.removeItem('role');
        localStorage.removeItem('songs');

        setIsAuthenticated(false);
        setUser(null);
        window.location.href = '/';
    };

    useEffect(() => {
        const role = localStorage.getItem('role');
        if (role) {
            setIsAuthenticated(true);
            setUserRole(role);
        }
    }, []);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setShowDropdown(false); // Close the dropdown
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, [dropdownRef]);

    const toggleDropdown = () => {
        setShowDropdown((prevShowDropdown) => !prevShowDropdown);
    };

    return (
        <nav>
            <div className="navbar-container">
                <div className="navbar-brand">
                    <Link to="/">WebName</Link>
                </div>
                <ul className="navbar-menu">
                    {!isAuthenticated && (
                        <>
                            <li><Link to="/register">Register</Link></li>
                            <li><Link to="/login">Login</Link></li>
                        </>
                    )}
                    {isAuthenticated && (
                        <>
                            {userRole === 'User' && (
                                <>
                                    <li><span className="username">User</span></li>
                                    <li><button onClick={onLogout}>Logout</button></li>
                                </>
                            )}
                            {userRole === 'Artist' && (
                                <>
                                    <li><Link to="/ListSong">List Song</Link></li>
                                    <li><Link to="/Song">Add Song</Link></li>
                                    <li><span className="username">Artist</span></li>
                                    <li><button onClick={onLogout}>Logout</button></li>
                                </>
                            )}
                            {userRole === 'Admin' && (
                                <>
                                    <li><span className="username">Admin</span></li>
                                    <li><button onClick={onLogout}>Logout</button></li>
                                </>
                            )}
                        </>
                    )}
                </ul>
            </div>
        </nav>
    );
};
export default Navbar;
