import authApi from './AxiosConfig';
const processOrder = async (orderId, totalAmount) => {
    console.log("Received orderId:", orderId);
    totalAmount = parseFloat(totalAmount.toFixed(2));
    console.log("Received totalAmount:", totalAmount); // Confirm totalAmount is passed
    try {
        // Convert totalAmount to a number if it's a string
        totalAmount = parseFloat(totalAmount);

        const paypalResponse = await createPayPalPayment(totalAmount);
        await updateOrderWithPayPalPaymentId(orderId, paypalResponse.id);
        window.location.href = paypalResponse.links.find(link => link.rel === 'approval_url').href;
    } catch (error) {
        console.error('Error processing order:', error);
        throw error;
    }
};

const createPayPalPayment = async (totalAmount) => {
    console.log("TotalAmount Type:", typeof totalAmount); // Check the data type
    console.log("TotalAmount Value:", totalAmount);

    try {
        const response = await authApi.post('/PayPal/CreatePayment', { totalAmount });
        return response.data;
    } catch (error) {
        console.error('Error creating PayPal payment:', error);
        throw error;
    }
}

const updateOrderWithPayPalPaymentId = async (orderId, paypalPaymentId) => {
    try {
        await authApi.put(`/Order/${orderId}`, { paypalPaymentId });
    } catch (error) {
        console.error('Error updating order with PayPal payment ID:', error);
        throw error;
    }
};


const addOrder = async (formData) => {
    try {
        const response = await authApi.post(`/Order`, formData);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
}

export {addOrder,processOrder}
