import React from 'react';
import { useCart } from './CartContext';
import { useNavigate } from 'react-router-dom';

const CartPage = () => {
    const { cartItems, removeFromCart } = useCart();
    const navigate = useNavigate();

    const handleDeleteItem = (index) => {
        removeFromCart(index);
    };

    const handleCheckout = () => {
        navigate('/checkout');
    };

    const calculateTotalPrice = () => {
        return cartItems.reduce((total, item) => {
            return total + (item.product.price * item.quantity);
        }, 0);
    };

    return (
        <div className="cart-container">
            <div className="cart-items">
                <h2>Your Cart</h2>
                {cartItems.map((item, index) => (
                    <div key={index} className="cart-item">
                        <img src={item.product.imageUrls[0]} alt={item.product.name} className="item-image" />
                        <div className="item-details">
                            <h3>{item.product.name}</h3>
                            <p>Price: {item.product.price} VND</p>
                            <p>Size: {item.size}</p>
                            <p>Quantity: {item.quantity}</p>
                            <p>Total Price: {item.product.price * item.quantity} VND</p>
                            <button onClick={() => handleDeleteItem(index)} className="remove-button">Remove</button>
                        </div>
                    </div>
                ))}
            </div>
            <div className="cart-summary">
                <h2>Order Summary</h2>
                <p>Total Price: {calculateTotalPrice()} VND</p>
                <button onClick={handleCheckout} className="checkout-button">Proceed to Checkout</button>
            </div>
        </div>
    );
};

export default CartPage;
