import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { updateSongById, deleteSongById, getSongById } from '../../Api/SongApi';

const EditSong = () => {
    const { songId } = useParams();
    const [song, setSong] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchSong = async () => {
            try {
                if (songId) { // Ensure songId is not undefined
                    const songData = await getSongById(songId);
                    setSong(songData);
                }
            } catch (error) {
                console.error('Error fetching song:', error);
            }
        };

        fetchSong();
    }, [songId]);

    const handleUpdate = async (updatedSongData) => {
        try {
            await updateSongById(songId, updatedSongData);
            window.location.reload(); // Refresh page after update
        } catch (error) {
            console.error('Error updating the song:', error);
        }
    };

    const handleDelete = async () => {
        try {
            await deleteSongById(songId);
            navigate('/ListSong'); // Correct path according to your Routes in App.js
        } catch (error) {
            console.error('Error deleting the song:', error);
        }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const formData = new FormData(event.target);
        const updatedSongData = {

        };
        handleUpdate(updatedSongData);
    };

    return (
        <div>
            {song && (
                <div>
                    <h2>Edit Song: {song.title}</h2>
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label htmlFor="title">Title:</label>
                            <input type="text" id="title" name="title" defaultValue={song.title} />
                        </div>
                        <button type="submit">Update Song</button>
                    </form>
                    <button onClick={handleDelete}>Delete Song</button>
                </div>
            )}
        </div>
    );
};

export default EditSong;
