import React, { useState, useEffect } from 'react';
import axios from 'axios';
import defaultImage from '../../Img/DefaultAvatar.png';

const ProductPage = () => {
    const [products, setProducts] = useState([]);
    const [newProduct, setNewProduct] = useState({});

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async ()  => {
        try {
            const response = await axios.get('/api/products');
            setProducts(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleInputChange = (e) => {
        setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
    };

    const handleAddProduct = async () => {
        try {
            await axios.post('/api/products', newProduct);
            fetchProducts(); // Refresh product list after adding
            setNewProduct({}); // Clear input fields
        } catch (error) {
            console.error('Error adding product:', error);
        }
    };

    const handleDeleteProduct = async (productId) => {
        try {
            await axios.delete(`/api/products/${productId}`);
            fetchProducts(); // Refresh product list after deletion
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            {/* Product List on the Left */}
            <div style={{ width: '50%' }}>
                <h1>Products</h1>
                <ul>
                    {products.map((product) => (
                        <li key={product.productId} onClick={() => handleSelectProduct(product)}>
                            {product.name} - {product.price}
                        </li>
                    ))}
                </ul>
            </div>

            {/* Product Details on the Right */}
            <div style={{ width: '50%' }}>
                {selectedProduct ? (
                    <div>
                        <h2>{selectedProduct.name}</h2>
                        <img src={defaultImage} alt="Product" />
                        {/* Display other details of selectedProduct here */}
                    </div>
                ) : (
                    <p>No product selected</p>
                )}
            </div>
        </div>
    );
};

export default ProductPage;
