// SearchPage.js
import React, { useState } from 'react';
import { searchByTitle } from "../Api/SongApi"; // Adjust import path as necessary
import { Link } from 'react-router-dom';

const SearchPage = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);

    const handleSearch = async () => {
        if (!searchQuery) return; // Don't search if query is empty
        try {
            const results = await searchByTitle(searchQuery);
            setSearchResults(results);
        } catch (error) {
            console.error('Error searching:', error);
            setSearchResults([]); // Reset/clear results on error
        }
    };

    const handleInputChange = (event) => {
        setSearchQuery(event.target.value);
    };

    return (
        <div>
            <h2>Search Songs</h2>
            <input
                type="text"
                value={searchQuery}
                onChange={handleInputChange}
                placeholder="Search by title..."
            />
            <button onClick={handleSearch}>Search</button>
            <div className="search-results">
                {searchResults.map((result) => (
                    <div key={result.songId}>
                        <Link to={`/song/${result.songId}`}>{result.title}</Link>
                    </div>
                ))}
            </div>
            <ul>
                {songs.map((song) => (
                    <li key={song.songId} className="song-item">
                        <div className="song-info">
                            <div className="image-container-ListSong">
                                {song.imageData && (
                                    <img src={`data:image/png;base64,${song.imageData}`} alt={`${song.title} cover art`} />
                                )}
                            </div>
                            <div className="song-details">
                                <strong>Title:</strong> {song.title}
                                {song.soundTrackData && (
                                    <div className="audio-player">
                                        <audio controls>
                                            <source src={`data:audio/mpeg;base64,${song.soundTrackData}`} type="audio/mpeg" />
                                            Your browser does not support the audio element.
                                        </audio>
                                    </div>
                                )}
                            </div>
                            <button onClick={() => handleEdit(song.songId)} className="edit-button">
                                Edit
                            </button>
                            <button onClick={() => handleDelete(song.songId)} className="delete-button">
                                Delete
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default SearchPage;
