import React, { useState, useEffect } from 'react';
import { useCart } from '../Cart/CartContext';
import {addOrder, processOrder} from '../../utils/Api/OrderApi';
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";

const CheckoutPage = () => {
    const { cartItems } = useCart();
    const [sdkReady, setSdkReady] = useState(false);
    const [customerInfo, setCustomerInfo] = useState({
        name: '',
        email: '',
        phoneNumber: '',
        address: ''
    });
    const [orderDetails, setOrderDetails] = useState({
        orderPlaced: false,
        orderId: '',
        vietQRData: ''
    });

    const calculateTotalPrice = () => cartItems.reduce((total, item) => (total + item.product.price * item.quantity), 0);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCustomerInfo(prev => ({ ...prev, [name]: value }));
    };
    const handlePlaceOrder = async () => {
        const totalPrice = calculateTotalPrice();
        console.log("Logging Total Price before processing order:", totalPrice);
        console.log("Value of totalAmount 1:", totalPrice);
        console.log("Data type of totalAmount 1:", typeof totalPrice);
        console.log('product id : ', cartItems.map(item=>({
            productId: item.productId
        })))
        const orderInfo = {
            userId: getUserIdFromLocalStorage(),
            cartItems: cartItems.map(item => ({
                productId: item.productId,
                quantity: item.quantity,
                size: item.size,
            })),
            ...customerInfo,
            totalPrice
        };
        console.log('Order data: ',orderInfo)
        try {
            const orderId = await addOrder(orderInfo);
            await processOrder(orderId, totalPrice);
            setOrderDetails({ orderPlaced: true, orderId });
        } catch (error) {
            console.error('Error placing order:', error);
        }
    };

    const getUserIdFromLocalStorage = () => {
        const userData = localStorage.getItem('user');
        return userData ? JSON.parse(userData).Id : null;
    };

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        if (storedUser) {
            setCustomerInfo({
                name: storedUser.UserName || '',
                email: storedUser.Email || '',
                phoneNumber: storedUser.PhoneNumber || '',
                address: storedUser.Address || ''
            });
        }
    }, []);

    useEffect(() => {
        if (window.paypal) {
            setSdkReady(true);
        } else {
            const interval = setInterval(() => {
                if (window.paypal) {
                    setSdkReady(true);
                    clearInterval(interval);
                }
            }, 100); // check every 100ms
            return () => clearInterval(interval);
        }
    }, []);
    const createOrder = (data, actions) => {
        return actions.order.create({
            purchase_units: [{
                amount: {
                    currency_code: 'USD', // Hardcoded currency code
                    value: (calculateTotalPrice()).toFixed(2) // Dynamic total amount
                }
            }]
        });
    };
    const onApprove = (data, actions) => actions.order.capture().then(details => {
        console.log('PayPal payment details:', details);
        handlePlaceOrder(); // Remove unnecessary object passing
    });
    return (
        <PayPalScriptProvider options={{ "client-id": "AXMl8juZd5UdMn3fqYuNTSH_NztoHa-mGVK0rlKY_lg6EnyIQE5_UA2osf0EwgVoyeNGMUNjYK6960QQ" }}>
            <div style={{ display: 'flex', justifyContent: 'center' }}>
                <div style={{ marginRight: '50px' }}>
                    <h2>Checkout</h2>
                    <form style={{ maxWidth: '400px', margin: 'auto' }}>
                        {['name', 'email', 'phoneNumber', 'address'].map(field => (
                            <div key={field} style={{ marginBottom: '15px' }}>
                                <label>{field.charAt(0).toUpperCase() + field.slice(1)}:</label>
                                <input
                                    type={field === 'email' ? 'email' : field === 'phoneNumber' ? 'tel' : 'text'}
                                    value={customerInfo[field]}
                                    onChange={handleInputChange}
                                    name={field}
                                    style={{ width: '100%', padding: '8px', fontSize: '16px', border: '1px solid #ccc', borderRadius: '5px' }}
                                />
                            </div>
                        ))}
                    </form>
                </div>
                <div>
                    <h2>Order Summary</h2>
                    <div>
                        {cartItems.map((item, index) => (
                            <div key={index} style={{ border: '1px solid #ccc', padding: '5px', marginBottom: '10px' }}>
                                <p>Product: {item.product.name}</p>
                                <p>Size: {item.size}</p>
                                <p>Quantity: {item.quantity}</p>
                                <p>Total Price: {(item.product.price * item.quantity).toLocaleString()} VND</p>
                            </div>
                        ))}
                    </div>
                    <p>Total Order Price: {calculateTotalPrice().toLocaleString()} USD</p>
                    <button onClick={() => setSdkReady(true)}>Proceed to PayPal</button>
                    {sdkReady && (
                        <PayPalButtons
                            createOrder={createOrder}
                            onApprove={onApprove}
                        />
                    )}
                </div>
                {orderDetails.orderPlaced && (
                    <div>
                        <h2>Order Placed Successfully!</h2>
                        <p>Order ID: {orderDetails.orderId}</p>
                    </div>
                )}
            </div>
        </PayPalScriptProvider>
    );
};

export default CheckoutPage;
