import axios from 'axios';

const baseLink = 'http://localhost:5163';

const api = axios.create({
    baseURL: baseLink,
    headers: {
        'Content-Type': 'application/json'
    }
});
api.interceptors.request.use((config) => {
    console.log("Interceptor hit for URL:", config.url); // Debugging
    const token = localStorage.getItem('token');
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
}, (error) => {
    return Promise.reject(error);
});



const login = async (credentials) => {
    try {
        const response = await axios.post('http://localhost:5163/Account/login', credentials);
        const token = response.data.token; // Extract the token
        localStorage.setItem('token', token); // Store the token in localStorage
        console.log('User logged in successfully.');
        return response.data; // Return the response data
    } catch (error) {
        console.error('Error:', error);
        throw error; // Throwing the error for further handling if necessary
    }
};


const register = async (userData) => {
    try {
        const response = await api.post('/Account/register', userData);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

const logout = async () => {
    try {
        const response = await api.post('/Account/logout');
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

const adminOnly = async () => {
    try {
        const response = await api.get('/Admin/admin-only');
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

const artistOnly = async () => {
    try {
        const response = await api.get('/Artist/artist-only');
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

const userOnly = async () => {
    try {
        const response = await api.get('/User/user-only');
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

export { login, register, logout, adminOnly, artistOnly, userOnly };
