import React, { useState, useEffect } from 'react';
import { getAllOrders } from '../../utils/Api/OrderApi';
import './OrderPage.css';
import {Link} from "react-router-dom"; // Import CSS for styling

const OrderPage = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        try {
            const ordersData = await getAllOrders();
            setOrders(ordersData);
        } catch (error) {
            console.error('Error fetching orders:', error);
        }
    };

    const handleSeeDetail = (orderId) => {

        console.log('Viewing details of order:', orderId);
    };

    return (
        <div className="order-container">
            <h1>All Orders</h1>
            {orders.length === 0 ? (
                <p>No orders available</p>
            ) : (
                <table className="order-table">
                    <thead>
                    <tr>
                        <th>Email</th>
                        <th>Total Price</th>
                        <th>Date</th>
                        <th>Phone Number</th>
                        <th>Actions</th> {/* Add column for actions */}
                    </tr>
                    </thead>
                    <tbody>
                    {orders.map(order => (
                        <tr key={order.orderId}>
                            <td>{order.email}</td>
                            <td>${order.totalPrice.toFixed(2)}</td>
                            <td>{new Date(order.dateTime).toLocaleString()}</td>
                            <td>{order.phoneNumber}</td>
                            <td>
                                <Link to={`/order/${order.orderId}`}> {/* Navigate to order detail page */}
                                    See Detail
                                </Link>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default OrderPage;
