import React, { useEffect, useState } from 'react';
import { getUserData, updateUserProfile } from "../../Api/UserApi"; // Adjust the path as needed
import defaultAvatar from '../../Img/DefaultAvatar.png'; // Ensure this path is correct
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import { storage } from "../../firebase"; // Adjust the path as needed

const ProfilePage = () => {
    const [user, setUser] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [newUsername, setNewUsername] = useState('');
    const [newImage, setNewImage] = useState(null);

    useEffect(() => {
        const fetchUserData = async () => {
            const storedUser = JSON.parse(localStorage.getItem('user'));
            const userId = storedUser?.id; // Ensure this matches the property name in your stored user object
            if (userId) {
                const userData = await getUserData(userId);
                setUser(userData);
                setNewUsername(userData.userName);
            }
        };

        fetchUserData();
    }, []);

    const handleEdit = () => {
        setIsEditing(true);
    };

    const handleImageChange = (event) => {
        setNewImage(event.target.files[0]);
    };

    const handleSave = async () => {
        if (!user) return;
        setIsEditing(true);
        const formData = new FormData();
        if (newUsername) {
            formData.append('Username', newUsername);
        }
        if (newImage) {
            const imageFileRef = ref(storage, `ProfileImage/${user.id}`);
            await uploadBytes(imageFileRef, newImage);
            const imageDownloadUrl = await getDownloadURL(imageFileRef);
            formData.append('ImageFile', imageDownloadUrl);
        }
        try {
            const updateResponse = await updateUserProfile(user.id, formData);
            // Assuming updateUserProfile response contains the updated user data
            setUser(updateResponse); // Update the local user state with the response
            setIsEditing(false);
        } catch (error) {
            console.error('Error updating profile:', error);
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Profile Page</h1>
            {!user ? (
                <div>No user information found.</div>
            ) : (
                <div style={{ maxWidth: '600px', margin: 'auto' }}>
                    <h2>User Information</h2>
                    <div>
                        <h3>Profile Picture</h3>
                        <img
                            src={user.imageFileName || defaultAvatar}
                            alt="Profile"
                            style={{
                                width: '250px',
                                height: '250px',
                                borderRadius: '50%',
                                objectFit: 'cover',
                            }}
                        />
                        <button onClick={handleEdit}>Edit Profile</button>
                    </div>

                    {isEditing && (
                        <div style={{ marginTop: '20px' }}>
                            <input
                                type="text"
                                value={newUsername}
                                onChange={(e) => setNewUsername(e.target.value)}
                            />
                            <input type="file" onChange={handleImageChange} />
                            <button onClick={handleSave}>Save Changes</button>
                        </div>
                    )}

                    <div style={{ marginBottom: '15px' }}>
                        <p><strong>Username:</strong> {user.userName}</p>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProfilePage;
