import React from 'react';
import defaultImage from '../../Img/DefaultAvatar.png';

const ProductPage = () => {
    const productList = [
        { id: 1, name: 'Product 1', inStock: true },
        { id: 2, name: 'Product 2', inStock: false },
        { id: 3, name: 'Product 3', inStock: true },
    ];

    const selectedProduct = null; // No data selected in this static view

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            {/* Product List on the Left */}
            <div style={{ width: '50%' }}>
                <h1>Product List</h1>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                    <thead>
                    <tr style={{ backgroundColor: '#f2f2f2' }}>
                        <th style={{ padding: '10px', textAlign: 'left' }}>ID</th>
                        <th style={{ padding: '10px', textAlign: 'left' }}>Name</th>
                        <th style={{ padding: '10px', textAlign: 'left' }}>Stock</th>
                    </tr>
                    </thead>
                    <tbody>
                    {productList.map((product) => (
                        <tr key={product.id} style={{ backgroundColor: 'white' }}>
                            <td style={{ padding: '10px', textAlign: 'left' }}>{product.id}</td>
                            <td style={{ padding: '10px', textAlign: 'left' }}>{product.name}</td>
                            <td style={{ padding: '10px', textAlign: 'left' }}>{product.inStock ? 'In Stock' : 'Out of Stock'}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>

            {/* Product Details on the Right */}
            <div style={{ width: '50%' }}>
                {selectedProduct ? (
                    <div>
                        <h2>{selectedProduct.name}</h2>
                        <img src={defaultImage} alt="Product" />
                        {/* Display other details of selectedProduct here */}
                    </div>
                ) : (
                    <p>No product selected</p>
                )}
            </div>
        </div>
    );
};

export default ProductPage;
