import React, {useEffect, useState} from 'react';
import { addNewSong } from '../../Api/SongApi';
import { useNavigate } from 'react-router-dom';
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import { storage } from "../../firebase";
import { Guid } from 'guid-typescript'; // Import Guid library

const AddPage = () => {
    const [title, setTitle] = useState('');
    const [soundtrackFile, setSoundtrackFile] = useState(null);
    const [imageFile, setImageFile] = useState(null);
    const [genre, setGenre] = useState('');
    const[duration,setDuration]=useState('');
    const [privacy, setPrivacy] = useState('public');
    const [isUploading, setIsUploading] = useState(false);
    const navigate = useNavigate();
    const user = JSON.parse(localStorage.getItem('user') || '{}');

    const songId = Guid.create().toString();

    // File URLs state
    const [soundtrackFileUrl, setSoundtrackFileUrl] = useState('');
    const [imageFileUrl, setImageFileUrl] = useState('');

    const genreToIntMap = {
        Workout: 0,
        Relaxation: 1,
        Party: 2,
        StudyFocus: 3,
        MoodBooster: 4,
        Chill: 5,
        Travel: 6,
        Morning: 7,
        Nighttime: 8,
        Throwback: 9,
        LoveSongs: 10,
        SadSongs: 11,
        Jazz: 12,
        Classical: 13,
        Indie: 14,
        Rock: 15,
        Pop: 16,
        HipHopRap: 17,
        Electronic: 18,
        RandBSoul: 19,
    };

    const handleTitleChange = (event) => setTitle(event.target.value);
    const handleGenreChange = (event) => setGenre(event.target.value);
    const handlePrivacyChange = (event) => {
        const selectedPrivacy = event.target.value;
        setPrivacy(selectedPrivacy === 'public');
    };

    const handleAudioFileUploadChange = (event) => {
        setSoundtrackFile(event.target.files[0]);
        const audio = new Audio();
        audio.src = URL.createObjectURL(event.target.files[0]);
        audio.addEventListener('loadedmetadata', () => {
            const durationAudio = audio.duration;
            console.log('Audio duration:', durationAudio);
            setSoundtrackFile(event.target.files[0]);
            setDuration(durationAudio);
        });
    };

    const handleImageFileUploadChange = (event) => {
        setImageFile(event.target.files[0]);
    };

    const addSong = async (event) => {
        event.preventDefault();
        const userRole = localStorage.getItem('role');
        if (userRole !== 'Artist') {
            alert('You must be an Artist to upload songs.');
            return;
        }
        setIsUploading(true);
        let audioDownloadURL = '';
        let imageDownloadURL = '';
        try {
            // Upload audio file and get its URL
            if (soundtrackFile) {
                const audioFileRef = ref(storage, `SoundTrack/${songId}`);
                await uploadBytes(audioFileRef, soundtrackFile);
                audioDownloadURL = await getDownloadURL(audioFileRef);
                console.log('Soundtrack File URL:', audioDownloadURL);
            }

            // Upload image file and get its URL
            if (imageFile) {
                const imageFileRef = ref(storage, `SoundTrackImage/${songId}`);
                await uploadBytes(imageFileRef, imageFile);
                imageDownloadURL = await getDownloadURL(imageFileRef);
                console.log('Image File URL:', imageDownloadURL);
            }


            // Prepare form data using the directly obtained URLs
            const formData = new FormData();
            formData.append('songId', songId);
            formData.append('title', title);
            formData.append('genreId', genreToIntMap[genre]);
            formData.append('privacy', privacy);
            formData.append('userId', user.Id);
            formData.append('imageUrl', imageDownloadURL); // Use direct URL
            formData.append('audioUrl', audioDownloadURL); // Use direct URL
            formData.append('duration', duration);

            for (let [key, value] of formData.entries()) {
                console.log(`${key}:`, value);
            }
            // Submit form data
            await addNewSong(formData);
            console.log('add new song success');
            // navigate('/'); // Redirect to homepage after adding the song
        } catch (error) {
            console.error('Failed to upload files or add the new song:', error);
        } finally {
            setIsUploading(false);
        }
    };

    useEffect(() => {
        if (soundtrackFileUrl) {
            console.log('audioUrl: ', soundtrackFileUrl);
        }
    }, [soundtrackFileUrl]);
    useEffect(() => {
        if (imageFileUrl) {
            console.log('imageUrl: ', imageFileUrl);
        }
    }, [imageFileUrl]); 

    const genreOptions = Object.keys(genreToIntMap).map(genreName => (
        <option key={genreName} value={genreName}>{genreName}</option>
    ));

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div style={{ width: '400px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)' }}>
                <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Add New Song</h2>
                <form onSubmit={addSong}>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Title:</label>
                        <input type="text" value={title} onChange={handleTitleChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Soundtrack File:</label>
                        <input type="file" name="soundtrack" onChange={handleAudioFileUploadChange} required />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Image File:</label>
                        <input type="file" name="image" onChange={handleImageFileUploadChange} required />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Genre:</label>
                        <select value={genre} onChange={handleGenreChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}>
                            <option value="">Select Genre</option>
                            {genreOptions}
                        </select>
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Privacy:</label>
                        <select value={privacy} onChange={handlePrivacyChange} style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}>
                            <option value="public">Public</option>
                            <option value="private">Private</option>
                        </select>
                    </div>
                    <button type="submit" style={{ width: '100%', padding: '10px', borderRadius: '5px', backgroundColor: '#007bff', color: '#fff', border: 'none', cursor: 'pointer' }}>Add Song</button>
                </form>
            </div>
        </div>
    );
};

export default AddPage;
