import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllAlbumByUserId } from '../../Api/ArtistApi';

const ListAlbum = () => {
    const [albums, setAlbums] = useState([]);
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const navigate = useNavigate();

    useEffect(() => {
        if (!user || !user.Id) {
            console.error('User ID is missing');
            return;
        }
        getAllAlbumByUserId(user.Id)
            .then(fetchedAlbums => {
                setAlbums(fetchedAlbums.flat());
            })
            .catch(error => console.error('Error fetching albums:', error));
    }, [user.Id]);

    const handleEdit = (albumId) => {
        console.log(`Editing album with ID: ${albumId}`);
        navigate(`/EditAlbum/${albumId}`);
    };

    const handleAddAlbum = () => {
        navigate('/AddAlbum');
    };

    return (
        <div>
            <div className="header-with-button">
                <h2>Your Albums</h2>
                <button className="add-album-button" onClick={handleAddAlbum}>Add Album</button>
            </div>
            <ul>
                {albums.map((album) => (
                    <div key={album.albumId} className="album-item">
                        <div className="album-info">
                            <div className="album-details">
                                {album.imageAlbumUrl && (
                                    <img src={album.imageAlbumUrl} alt={`${album.nameOfAlbum} cover art`} className="image-container-ListAlbum" /> // Update here
                                )}
                                <strong>Title:</strong> {album.nameOfAlbum}
                            </div>
                            <button onClick={() => handleEdit(album.albumId)} className="edit-button">
                                Edit
                            </button>
                        </div>
                    </div>
                ))}
            </ul>
            <style>
                {`
            .header-with-button {
                display: flex;
                justify-content: space-between;
                align-items: center;
                margin-bottom: 20px;
            }
            .add-album-button {
                padding: 10px 20px;
                background-color: #007bff;
                color: #fff;
                border: none;
                border-radius: 5px;
                cursor: pointer;
            }
            .add-album-button:hover {
                background-color: #0056b3;
            }
            .navbar-container {
              display: flex;
              justify-content: space-between;
              align-items: center;
              padding: 1rem;
              background-color: #333;
              color: #fff;
            }
            .navbar-menu {
              list-style: none;
              display: flex;
              align-items: center;
            }
            .navbar-menu li {
              margin-left: 1rem;
            }
            .navbar-menu li:first-child {
              margin-left: 0;
            }
            .navbar-menu a {
              color: #fff;
              text-decoration: none;
            }
            button {
              padding: 10px 20px;
              margin: 5px;
              background-color: #007bff;
              color: #fff;
              border: none;
              border-radius: 5px;
              cursor: pointer;
            }
            button:hover {
              background-color: #0056b3;
            }

            `}
            </style>
        </div>
    );
};

export default ListAlbum;
