import authApi from './AxiosConfig';

const addOrder = async (orderInfo) => {
    try {
        const response = await authApi.post(`/Order`, orderInfo);
        return response.data.id; // Assuming the response contains the ID of the newly created order
    } catch (error) {
        console.error('Error adding order:', error);
        throw error;
    }
};

const processOrder = async (orderId, totalPrice) => {
    try {
        const paypalResponse = await createPayPalPayment(totalPrice);
        await updateOrderWithPayPalPaymentId(orderId, paypalResponse.id);
        window.location.href = paypalResponse.links.find(link => link.rel === 'approval_url').href;
    } catch (error) {
        console.error('Error processing order:', error);
        throw error;
    }
};

const createPayPalPayment = async (totalAmount) => {
    try {
        const parsedTotalAmount = parseFloat(totalAmount).toFixed(2);
        if (isNaN(parsedTotalAmount) || parsedTotalAmount <= 0) {
            throw new Error('Invalid totalAmount value');
        }

        const response = await authApi.post('/PayPal/CreatePayment', { totalAmount: parsedTotalAmount });
        return response.data;
    } catch (error) {
        console.error('Error creating PayPal payment:', error);
        throw error;
    }
};

const updateOrderWithPayPalPaymentId = async (orderId, paypalPaymentId) => {
    try {
        await authApi.put(`/Order/${orderId}`, { paypalPaymentId });
    } catch (error) {
        console.error('Error updating order with PayPal payment ID:', error);
        throw error;
    }
};

export { addOrder, processOrder };
