import React, { useState } from 'react';
import { addNewAlbum } from '../../Api/ArtistApi';
import { useNavigate } from 'react-router-dom';
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import { storage } from "../../firebase";
import defaultAlbumImage from '../../Img/DefaultAvatar.png'; // Import default album image

const AddAlbumPage = () => {
    const [albumName, setAlbumName] = useState('');
    const [albumImage, setAlbumImage] = useState(null);
    const [imagePreview, setImagePreview] = useState(defaultAlbumImage); // Set default image preview
    const navigate = useNavigate();

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setAlbumImage(file);
            const previewUrl = URL.createObjectURL(file);
            setImagePreview(previewUrl); // Set image preview
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Validate album name and image before submission
            if (!albumName || !albumImage) {
                alert('Album name and image are required.');
                return;
            }

            const formData = new FormData();
            formData.append('NameOfAlbum', albumName);

            const imageFileRef = ref(storage, `AlbumImage/${albumImage.name}`);
            await uploadBytes(imageFileRef, albumImage);
            const imageDownloadUrl = await getDownloadURL(imageFileRef);
            formData.append('ImageAlbumUrl', imageDownloadUrl);

            const result = await addNewAlbum(formData);
            console.log(result);
            alert('Album added successfully!');
            navigate('/ListAlbum');

        } catch (error) {
            console.error('Error adding album:', error);
            alert('Failed to add album.');
        }
    };

    return (
        <div>
            <h2>Add New Album</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="albumName">Album Name *</label>
                    <input
                        id="albumName"
                        type="text"
                        value={albumName}
                        onChange={(e) => setAlbumName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="albumImage">Album Image *</label>
                    <input
                        id="albumImage"
                        type="file"
                        onChange={handleImageChange}
                        required
                    />
                </div>
                {imagePreview && (
                    <div>
                        <h3>Image Preview</h3>
                        <img src={imagePreview} alt="Album Preview" style={{ maxWidth: '200px' }} />
                    </div>
                )}
                <button type="submit">Add Album</button>
            </form>
        </div>
    );
};

export default AddAlbumPage;
