import React, { useState } from 'react';
import { searchOrder } from "../../utils/Api/OrderApi";

const SearchOrder = () => {
    const [email, setEmail] = useState('');
    const [orderId, setOrderId] = useState('');
    const [orderData, setOrderData] = useState(null); // State to store fetched order data

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const data = { orderId, email };
            const order = await searchOrder(data);
            setOrderData(order); // Set the fetched order data to state
            console.log('Search result:', order);
        } catch (error) {
            console.error('Error searching for order:', error);
        }
    };

    return (
        <div>
            <h2>Search Order</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Order ID:</label>
                    <input
                        type="text"
                        value={orderId}
                        onChange={(e) => setOrderId(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Search</button>
            </form>

            {orderData && ( // Render order information if data is available
                <div>
                    <h3>Order Information</h3>
                    <p>Order ID: {orderData.orderId}</p>
                    <p>Email: {orderData.email}</p>
                    <p>Name: {orderData.name}</p>
                    <p>Phone Number: {orderData.phoneNumber}</p>
                    <p>Address: {orderData.address}</p>
                    <p>Status: {orderData.status}</p>
                    <p>Total Price: {orderData.totalPrice}</p>
                    <p>Date/Time: {orderData.dateTime}</p>
                    <h4>Order Items:</h4>
                    <ul>
                        {orderData.orderItems.map(item => (
                            <li key={item.orderItemId}>
                                <p>Product Name: {item.productName}</p>
                                <p>Quantity: {item.quantity}</p>
                                <p>Size: {item.size}</p>
                                <p>Total Price: {item.totalPrice}</p>
                                {item.imageUrl && (
                                    <img src={item.imageUrl[0]} alt={item.productName} style={{ width: '100px' }} />
                                )}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default SearchOrder;
