import React, { useState, useEffect } from 'react';
import { useCart } from '../Cart/CartContext';
import {addOrder} from "../../utils/Api/OrderApi";
import QRCode from 'qrcode.react';
const CheckoutPage = () => {
    const { cartItems } = useCart();
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [address, setAddress] = useState('');
    const [orderPlaced, setOrderPlaced] = useState(false);
    const [orderId, setOrderId] = useState('');
    const [vietQRData, setVietQRData] = useState('');
    const storedUser = JSON.parse(localStorage.getItem('user'));

    const calculateTotalPrice = () => {
        return cartItems.reduce((total, item) => {
            return total + (item.product.price * item.quantity);
        }, 0);
    };
    const handlePlaceOrder = async () => {
        try {
            const userId = storedUser?.Id;

            console.log('UserId: ',userId)
            console.log("product items: ", cartItems)
            console.log("Name:", name);
            console.log("Email:", email);
            console.log("Phone Number:", phoneNumber);
            console.log("Address:", address);
            console.log("Total price: ", calculateTotalPrice());
            const totalPrice= calculateTotalPrice();
            const orderInfor = {
                userId,
                cartItems: cartItems.map(item => ({
                    ProductId: item.product.productId,
                    Size: item.size,
                    Quantity: parseInt(item.quantity),
                    TotalPrice: item.product.price * parseInt(item.quantity)
                })),
                name,
                email,
                phoneNumber,
                address,
                totalPrice
            };

            const order = await addOrder(orderInfor);
            console.log('order response: ',order)
            setOrderPlaced(true);
            setOrderId(order.id);

            // Generate VietQR data
            const bankCode = "970422";  // Your bank code
            const accountNumber = "0395909420";  // Your account number
            const amount = totalPrice/100;  // Total price or a specific amount
            const reference = order.id;  // Use order ID as reference
            const formattedVietQR = `BCD\n001\n2\nSCT\n${bankCode}\n${accountNumber}\nVND\n${amount}\n${reference}`;
            setVietQRData(formattedVietQR);
        }
         catch (error) {
            console.error('Error saving product:', error);
        }
    };

    // Fetch user data from localStorage on component mount
    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        if (storedUser) {
            setName(storedUser.UserName || '');
            setEmail(storedUser.Email || '');
            setPhoneNumber(storedUser.PhoneNumber || '');
            setAddress(storedUser.Address || '');
        }
    }, []);

    return (
        <div style={{ display: 'flex', justifyContent: 'center' }}>
            {/* Left Section - User Information Form */}
            <div style={{ marginRight: '50px' }}>
                <h2>Checkout</h2>
                <form style={{ maxWidth: '400px', margin: 'auto' }}>
                    <div style={{ marginBottom: '15px' }}>
                        <label style={{ display: 'block', marginBottom: '5px' }}>Name:</label>
                        <input type="text" value={name} onChange={(e) => setName(e.target.value)} style={{ width: '100%', padding: '8px', fontSize: '16px', border: '1px solid #ccc', borderRadius: '5px' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label style={{ display: 'block', marginBottom: '5px' }}>Email:</label>
                        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} style={{ width: '100%', padding: '8px', fontSize: '16px', border: '1px solid #ccc', borderRadius: '5px' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label style={{ display: 'block', marginBottom: '5px' }}>Phone Number:</label>
                        <input type="tel" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} style={{ width: '100%', padding: '8px', fontSize: '16px', border: '1px solid #ccc', borderRadius: '5px' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label style={{ display: 'block', marginBottom: '5px' }}>Address:</label>
                        <textarea value={address} onChange={(e) => setAddress(e.target.value)} style={{ width: '100%', padding: '8px', fontSize: '16px', border: '1px solid #ccc', borderRadius: '5px', minHeight: '100px' }}></textarea>
                    </div>
                </form>
            </div>
            {/* Right Section - Summary of Order */}
            <div>
                <h2>Order Summary</h2>
                <div>
                    {cartItems.map((item, index) => (
                        <div key={index} style={{border: '1px solid #ccc',padding:'5px',marginBottom:'10px'}}>
                            <p>Product: {item.product.name}</p>
                            <p>Size: {item.size}</p>
                            <p>Quantity: {item.quantity}</p>
                            <p>Total Price: {(item.product.price * item.quantity).toLocaleString()} VND</p>
                        </div>
                    ))}
                </div>
                <p>Total Order Price: {calculateTotalPrice().toLocaleString()} VND</p>
                <button onClick={handlePlaceOrder}>Place Order</button>
            </div>
            {orderPlaced && (
                <div>
                    <h2>Order Placed Successfully!</h2>
                    <p>Order ID: {orderId}</p>
                    <QRCode value={vietQRData} size={256} />  {/* Generate QR Code */}
                </div>
            )}
        </div>
    );
};

export default CheckoutPage;
